{
  "$schema": "https://anthropic.com/claude-code/marketplace.schema.json",
  "name": "cc-local-marketplace",
  "version": "1.0.0",
  "description": "Local marketplace for Claude Code plugins",
  "owner": {
    "name": "Neo",
    "email": "neo@tokenz.local"
  },
  "plugins": [
    {
      "name": "stackey-backend",
      "description": "Skills for working with StackeyBackend Supabase API - blocks, media, favorites, and authentication",
      "version": "1.0.0",
      "source": "./stackey-backend",
      "category": "integration"
    },
    {
      "name": "swift-project-tools",
      "description": "Skills for setting up and extending Swift Project Tools in iOS/Swift projects",
      "source": "./swift-project-tools",
      "category": "ios"
    },
    {
      "name": "stackey-experts",
      "description": "Expert agents for StackeyApp (iOS/Swift), StackeyBackend (Supabase/Deno), and API contract management with cross-component coordination",
      "version": "1.0.0",
      "source": "./stackey-experts",
      "category": "integration"
    },
    {
      "name": "agent-orchestration",
      "description": "Protocol for dispatching, monitoring, and recovering from subagent failures — pre-flight checks, retry policy, fallback strategy, output contracts, and team swarm coordination",
      "version": "0.1.0",
      "source": "./agent-orchestration",
      "category": "workflow"
    },
    {
      "name": "session-manager",
      "description": "Terminal session management - fork sessions, run commands in panes/tabs, capture output",
      "version": "2.1.0",
      "source": "./session-manager",
      "category": "development"
    },
    {
      "name": "claude-usage-analyzer",
      "description": "Analyze Claude Code session usage data and generate comprehensive reports",
      "version": "1.1.0",
      "source": "./claude-usage-analyzer",
      "category": "analytics"
    },
    {
      "name": "default-tools",
      "description": "Auto-approve safe tools with sensitive-path guards, macOS notifications, and session retrospective command",
      "version": "1.2.0",
      "source": "./default-tools",
      "category": "workflow"
    },
    {
      "name": "ios-agentic-loop",
      "description": "Agentic iOS testing system combining idb-driven UI exploration with Maestro deterministic regression. Provides skills for accessibility auditing, Maestro flow authoring, and autonomous idb observe-reason-act-verify loops.",
      "version": "1.0.0",
      "source": "./ios-agentic-loop",
      "category": "ios"
    }
  ]
}
