{
  "$schema": "https://anthropic.com/claude-code/marketplace.schema.json",
  "name": "ty-claude-plugin",
  "description": "Python type checker LSP plugin using ty by Astral",
  "owner": {
    "name": "KRRT7"
  },
  "plugins": [
    {
      "name": "ty-lsp",
      "description": "Python language server using ty - an extremely fast type checker (10-100x faster than mypy/Pyright) by Astral",
      "version": "1.0.0",
      "author": {
        "name": "Astral Software Inc."
      },
      "source": {
        "source": "github",
        "repo": "KRRT7/ty-claude-plugin"
      },
      "category": "development",
      "strict": false,
      "homepage": "https://github.com/astral-sh/ty",
      "tags": ["community-managed"],
      "lspServers": {
        "ty": {
          "command": "ty",
          "args": ["server"],
          "extensionToLanguage": {
            ".py": "python",
            ".pyi": "python"
          }
        }
      }
    }
  ]
}

