{
  "$schema": "https://code.claude.com/schemas/marketplace.json",
  "name": "leefowlercu-agent-kit",
  "owner": {
    "name": "Lee Fowler"
  },
  "metadata": {
    "description": "LeeFowlerCU's Agent Kit",
    "version": "1.0.0"
  },
  "plugins": [
    {
      "name": "agent-skill-builder",
      "description": "Assists in creating Agent Skills of varying complexity levels (simple, moderate, complex)",
      "version": "0.1.0",
      "author": {
        "name": "Lee Fowler"
      },
      "keywords": ["skills", "agents", "development", "scaffolding"],
      "category": "development",
      "license": "MIT",
      "strict": false,
      "source": "./plugins/agent-skill-builder"
    },
    {
      "name": "project-docs-reconciler",
      "description": "Provides slash commands to initialize or reconcile project documentation (README.md, CLAUDE.md, subsystem docs)",
      "version": "0.1.1",
      "author": {
        "name": "Lee Fowler"
      },
      "keywords": ["documentation", "readme", "claude-md", "reconciliation"],
      "category": "documentation",
      "license": "MIT",
      "strict": false,
      "source": "./plugins/project-docs-reconciler"
    },
    {
      "name": "spec-interviewer-planner",
      "description": "Commands for interviewing about specifications and generating implementation plans",
      "version": "0.1.1",
      "author": {
        "name": "Lee Fowler"
      },
      "keywords": ["specification", "interview", "planning", "implementation"],
      "category": "productivity",
      "license": "MIT",
      "strict": false,
      "source": "./plugins/spec-interviewer-planner"
    },
    {
      "name": "op-secrets-manager",
      "description": "Skills for interacting with 1Password through the op CLI",
      "version": "0.1.0",
      "author": {
        "name": "Lee Fowler"
      },
      "keywords": ["1password", "secrets", "credentials", "op", "cli"],
      "category": "security",
      "license": "MIT",
      "strict": false,
      "source": "./plugins/op-secrets-manager"
    },
    {
      "name": "gtasks-todo-manager",
      "description": "Manages to-dos across multiple Google accounts using the Google Tasks API",
      "version": "0.4.0",
      "author": {
        "name": "Lee Fowler"
      },
      "keywords": ["google-tasks", "todo", "task-management", "productivity", "multi-account"],
      "category": "productivity",
      "license": "MIT",
      "strict": false,
      "source": "./plugins/gtasks-todo-manager"
    }
  ]
}
