{
  "name": "kana-code-plugins",
  "owner": {
    "name": "Linaqruf"
  },
  "metadata": {
    "description": "Claude Code plugins by Linaqruf",
    "version": "1.0.0",
    "pluginRoot": "./plugins",
    "repository": "https://github.com/Linaqruf/kana-code-plugins"
  },
  "plugins": [
    {
      "name": "project-spec",
      "source": "./plugins/project-spec",
      "description": "Generate project, feature, and design specifications with a single /spec-writing command. Features adaptive routing, gap analysis, design audit, and codebase-aware interviews.",
      "version": "4.0.0",
      "author": {
        "name": "Linaqruf"
      },
      "license": "MIT",
      "keywords": ["planning", "specification", "design-system", "feature-planning", "interview", "project-init", "documentation", "gap-analysis", "design-audit"],
      "category": "productivity"
    },
    {
      "name": "suno-composer",
      "source": "./plugins/suno-composer",
      "description": "Creative or guided workflow for composing Suno AI songs. Vision-first mode (Claude proposes, you react) or guided mode (step-by-step wizard). Features narrative style prompts, artist profiles, J-pop tier presets, adaptive preference learning, conversational intent detection, album mode, variations, and song continuations.",
      "version": "5.5.0",
      "author": {
        "name": "Linaqruf"
      },
      "license": "MIT",
      "keywords": ["suno", "music", "composition", "lyrics", "ai-music", "j-pop", "vocaloid", "city-pop", "song-generation", "album", "remix", "artist-reference", "anisong", "doujin", "vision-first", "dual-mode", "preferences", "adaptive-learning", "narrative-prompts"],
      "category": "creative"
    },
    {
      "name": "kana-code-rpc",
      "source": "./plugins/kana-code-rpc",
      "description": "Display Claude Code activity as Discord Rich Presence. Shows project name, current tool activity, model, token usage, cost, git branch, lines changed, agent name, and context warnings. Features session_id tracking, idle detection, MCP tool support, and YAML configuration with hot-reload.",
      "version": "0.4.0",
      "author": {
        "name": "Linaqruf"
      },
      "license": "MIT",
      "keywords": ["discord", "rich-presence", "activity", "status", "daemon", "hooks", "tokens", "cost", "multi-session"],
      "category": "integration"
    }
  ]
}
