      "author": {
        "name": "linehaulai"
      },
      "keywords": ["go", "golang", "testing", "tdd", "testify", "testcontainers", "unit-tests", "integration-tests"]
    },
    {
      "name": "testcontainers-go",
      "source": "./plugins/testcontainers-go",
      "version": "1.0.0",
      "description": "Guide for using Testcontainers for Go with 62+ pre-configured modules for integration testing",
      "author": {
        "name": "linehaulai"
      },
      "keywords": ["go", "golang", "testcontainers", "integration-testing", "docker", "containers"]
    },
    {
      "name": "deep-wiki",
      "source": "./plugins/deep-wiki",
      "version": "2.0.0",
