{
  "name": "professional-status",
  "version": "1.0.0",
  "description": "Makes Claude Code status messages professional rather than playful.",
  "hooks": {
    "SessionStart": [
      {
        "hooks": [
          {
            "type": "prompt",
            "prompt": "Use concise, professional language in all progress or status updates.\n\nAvoid playful or whimsical wording such as 'hustling', 'frolicking', 'vibing', or similar expressions.\n\nUse neutral wording such as:\n- Processing\n- Analyzing\n- Writing changes\n- Updating files"
          }
        ]
      }
    ]
  }
}
