{
  "$schema": "https://anthropic.com/claude-code/marketplace.schema.json",
  "name": "meetocean-voice",
  "description": "Voice AI plugin for Claude Code — talk to Claude about your codebase through your mic or join conference calls as an AI assistant",
  "owner": {
    "name": "MeetOcean AI",
    "email": "artem@meetocean.ai"
  },
  "plugins": [
    {
      "name": "codebase-voice-assistant",
      "source": "./",
      "description": "Talk to Claude about your codebase using voice. Free local mode (mic + speakers) or Pro mode (dial into conference calls via SIP).",
      "version": "0.1.0",
      "author": {
        "name": "Artem Ustimenko",
        "email": "artem@meetocean.ai"
      },
      "homepage": "https://github.com/meetocean-ai/codebase-voice-assistant",
      "repository": "https://github.com/meetocean-ai/codebase-voice-assistant",
      "license": "MIT",
      "category": "productivity",
      "keywords": ["voice", "codebase", "standup", "meetings", "conference-call", "speech", "hands-free"],
      "strict": false,
      "skills": [
        "./skills/voice-start",
        "./skills/voice-join",
        "./skills/voice-config",
        "./skills/voice-status"
      ],
      "mcpServers": {
        "codebase-voice": {
          "type": "stdio",
          "command": "${CLAUDE_PLUGIN_ROOT}/.venv/bin/python",
          "args": ["-m", "src"],
          "cwd": "${CLAUDE_PLUGIN_ROOT}",
          "env": {
            "DEEPGRAM_API_KEY": "${DEEPGRAM_API_KEY}",
            "LIVEKIT_URL": "${LIVEKIT_URL:-}",
            "LIVEKIT_API_KEY": "${LIVEKIT_API_KEY:-}",
            "LIVEKIT_API_SECRET": "${LIVEKIT_API_SECRET:-}",
            "TWILIO_ACCOUNT_SID": "${TWILIO_ACCOUNT_SID:-}",
            "TWILIO_AUTH_TOKEN": "${TWILIO_AUTH_TOKEN:-}"
          }
        }
      }
    }
  ]
}

