{
  "name": "wicked-garden",
  "owner": {
    "name": "Mike Parcewski"
  },
  "plugins": [
    {
      "name": "wicked-mem",
      "source": "./plugins/wicked-mem",
      "description": "Structured memory with signal-based recall. Unlike built-in memory, wicked-mem categorizes memories by type (episodic, decision, procedural, preference), decays stale memories automatically, and only injects context when your question signals need it \u2014 preventing context bloat.",
      "version": "1.1.0"
    },
    {
      "name": "wicked-search",
      "source": "./plugins/wicked-search",
      "description": "Unified code and document search with Symbol Graph, graph traversal, hotspot detection, and multi-project isolation. Multi-ORM data lineage, cross-layer linking, blast radius analysis, symbol enrichment, and wicked-cache integration.",
      "version": "2.2.0"
    },
    {
      "name": "wicked-patch",
      "source": "./plugins/wicked-patch",
      "description": "Language-agnostic code generation and change propagation using symbol graphs. 12 generators (Java, Python, TypeScript, Go, C#, Ruby, Kotlin, Rust, PHP, Perl, JSP, SQL) with safety validation and transactional rollback.",
      "version": "2.1.1"
    },
    {
      "name": "wicked-kanban",
      "source": "./plugins/wicked-kanban",
      "description": "Persistent kanban board that survives across sessions. Auto-syncs Claude Code's TaskCreate/TaskUpdate to a durable board with git commit linking, task comments API, activity audit trail, and sprint grouping. Built-in tasks reset each session \u2014 kanban remembers everything.",
      "version": "1.1.0"
    },
    {
      "name": "wicked-crew",
      "source": "./plugins/wicked-crew",
      "description": "Workflow coordinator with quality gates for intent-driven software delivery. Orchestrates multi-phase projects with embedded QE gates, project archiving, and signal-based specialist routing.",
      "version": "1.1.0"
    },
    {
      "name": "wicked-jam",
      "source": "./plugins/wicked-jam",
      "description": "AI-powered brainstorming with dynamic focus groups. Generate diverse perspectives on ideas through persona-based discussion rounds.",
      "version": "1.1.0"
    },
    {
      "name": "wicked-data",
      "source": "./plugins/wicked-data",
      "description": "Data engineering specialist for pipeline design, data analysis, ML guidance, and analytics architecture. Includes CSV/Excel analysis with DuckDB SQL querying.",
      "version": "1.1.0"
    },
    {
      "name": "wicked-startah",
      "source": "./plugins/wicked-startah",
      "description": "Opinionated starter kit with MCP servers and plugin recommendations for a productive Claude Code setup",
      "version": "0.10.0"
    },
    {
      "name": "wicked-workbench",
      "source": "./plugins/wicked-workbench",
      "description": "Build-your-own dashboard for wicked-garden plugins using A2UI protocol. Combines components from kanban, memory, and other plugins into unified, AI-generated interfaces.",
      "version": "1.1.0"
    },
    {
      "name": "wicked-qe",
      "source": "./plugins/wicked-qe",
      "description": "Quality Engineering specialist for test strategy, scenario generation, test automation, and shift-left quality practices.",
      "version": "1.1.0"
    },
    {
      "name": "wicked-engineering",
      "source": "./plugins/wicked-engineering",
      "description": "Engineering expertise with commands for planning, review, debugging, architecture, and docs. Includes senior engineers, architects, and technical writers.",
      "version": "1.1.0"
    },
    {
      "name": "wicked-product",
      "source": "./plugins/wicked-product",
      "description": "Product and design expertise spanning requirements, UX, customer voice, and business strategy. Includes product managers, designers, researchers, and strategists.",
      "version": "1.1.0"
    },
    {
      "name": "wicked-platform",
      "source": "./plugins/wicked-platform",
      "description": "Platform engineering expertise spanning DevSecOps, observability, and compliance. Includes SREs, security engineers, compliance officers, and infrastructure specialists.",
      "version": "1.1.0"
    },
    {
      "name": "wicked-delivery",
      "source": "./plugins/wicked-delivery",
      "description": "Delivery management expertise spanning PMO, experimentation, effort allocation, and risk monitoring. Includes delivery managers, cost analysts, rollout coordinators, and phase/signal effort breakdowns.",
      "version": "1.1.0"
    },
    {
      "name": "wicked-smaht",
      "source": "./plugins/wicked-smaht",
      "description": "Ecosystem multiplier \u2014 automatically gathers context from installed plugins (memories, tasks, code search, brainstorms) before Claude responds. The more plugins you install, the smarter it gets. Confidence-based routing: fast path for simple queries, comprehensive assembly for complex ones.",
      "version": "3.1.0"
    },
    {
      "name": "wicked-agentic",
      "source": "./plugins/wicked-agentic",
      "description": "Agentic architecture review, design guidance, and optimization for AI systems built with LLMs, agents, and workflows. Detects frameworks, analyzes agent topologies, scores patterns, and provides actionable recommendations.",
      "version": "2.1.0"
    },
    {
      "name": "wicked-scenarios",
      "source": "./plugins/wicked-scenarios",
      "description": "E2E testing via human and agent-readable markdown scenarios that orchestrate lightweight open-source CLI tools. Covers API, browser, performance, infrastructure, security, and accessibility testing with graceful degradation when tools are missing.",
      "version": "1.1.0"
    }
  ]
}
