#!/usr/bin/env python3
"""
External Rules Injection Hook

Reads rules from other AI IDEs and injects them into Claude Code context.
Supports: Cursor (.cursor/rules/*.mdc), Windsurf (.windsurfrules),
          Copilot (.github/copilot-instructions.md), Aider (CONVENTIONS.md)
"""

import os
import sys
import json
import glob
from pathlib import Path


def load_settings():
    """Load settings from .claude/external-rules.local.md"""
    settings_file = os.path.join(os.getcwd(), '.claude', 'external-rules.local.md')

    defaults = {
        'enabled': False,
        'cursor': False,
        'windsurf': False,
        'copilot': False,
        'aider': False,
    }

    if not os.path.isfile(settings_file):
        return defaults

    try:
        with open(settings_file, 'r', encoding='utf-8') as f:
            content = f.read()

        if content.startswith('---'):
            parts = content.split('---', 2)
            if len(parts) >= 2:
                for line in parts[1].strip().split('\n'):
                    if ':' in line:
                        key, value = line.split(':', 1)
                        key = key.strip()
                        value = value.strip().lower()
                        if key in defaults:
                            defaults[key] = value == 'true'
        return defaults
    except Exception:
        return defaults


def parse_file(file_path):
    """Parse a markdown/mdc file, extracting content after frontmatter."""
    try:
        with open(file_path, 'r', encoding='utf-8') as f:
            content = f.read()

        # Remove YAML frontmatter if present
        if content.startswith('---'):
            parts = content.split('---', 2)
            if len(parts) >= 3:
                return parts[2].strip()
        return content.strip()
    except Exception:
        return None


def load_cursor_rules():
    """Load rules from .cursor/rules/*.mdc"""
    rules = []
    rules_dir = os.path.join(os.getcwd(), '.cursor', 'rules')

    if not os.path.isdir(rules_dir):
        return rules

    for ext in ['*.mdc', '*.md']:
        for file_path in glob.glob(os.path.join(rules_dir, '**', ext), recursive=True):
            content = parse_file(file_path)
            if content:
                name = Path(file_path).stem
                rules.append((name, file_path, content))

    return rules


def load_windsurf_rules():
    """Load rules from .windsurfrules"""
    rules = []
    rules_file = os.path.join(os.getcwd(), '.windsurfrules')

    if os.path.isfile(rules_file):
        content = parse_file(rules_file)
        if content:
            rules.append(('windsurfrules', rules_file, content))

    return rules


def load_copilot_rules():
    """Load rules from .github/copilot-instructions.md"""
    rules = []
    rules_file = os.path.join(os.getcwd(), '.github', 'copilot-instructions.md')

    if os.path.isfile(rules_file):
        content = parse_file(rules_file)
        if content:
            rules.append(('copilot-instructions', rules_file, content))

    return rules


def load_aider_rules():
    """Load rules from CONVENTIONS.md"""
    rules = []
    rules_file = os.path.join(os.getcwd(), 'CONVENTIONS.md')

    if os.path.isfile(rules_file):
        content = parse_file(rules_file)
        if content:
            rules.append(('conventions', rules_file, content))

    return rules


def format_rules(rules_by_source):
    """Format all rules into a context string."""
    if not any(rules_by_source.values()):
        return ""

    output = ["# External AI IDE Rules", ""]
    output.append("The following rules have been imported from other AI coding assistants.")
    output.append("Please follow these guidelines.\n")

    source_names = {
        'cursor': 'Cursor',
        'windsurf': 'Windsurf',
        'copilot': 'GitHub Copilot',
        'aider': 'Aider'
    }

    for source, rules in rules_by_source.items():
        if not rules:
            continue

        output.append(f"## From {source_names.get(source, source)}\n")

        for name, file_path, content in rules:
            output.append(f"### {name}")
            output.append(f"*Source: {file_path}*\n")
            output.append(content)
            output.append("")

    return "\n".join(output)


def main():
    """Main hook function."""
    try:
        json.load(sys.stdin)
    except Exception:
        pass

    settings = load_settings()

    if not settings.get('enabled', False):
        print(json.dumps({}))
        return

    rules_by_source = {}

    if settings.get('cursor'):
        rules_by_source['cursor'] = load_cursor_rules()

    if settings.get('windsurf'):
        rules_by_source['windsurf'] = load_windsurf_rules()

    if settings.get('copilot'):
        rules_by_source['copilot'] = load_copilot_rules()

    if settings.get('aider'):
        rules_by_source['aider'] = load_aider_rules()

    context = format_rules(rules_by_source)

    if context:
        print(json.dumps({"systemMessage": context}))
    else:
        print(json.dumps({}))


if __name__ == '__main__':
    main()
