import { useRef } from 'react';

export function useAudio(src: string) {
  const audioRef = useRef<HTMLAudioElement>(new Audio(src));

  const play = () => {
    audioRef.current.play();
  };
  const stop = () => {
    audioRef.current.pause();
    audioRef.current.currentTime = 0;
  };

  return { play, stop };
}

import { useState, useEffect, createRef } from 'react';
import * as THREE from 'three';
import { generateRandomPositions } from '../utils/randomPosition';

export interface BallData {
  id: number;
  number: number;
  position: [number, number, number];
  mesh: React.RefObject<THREE.Mesh>;
}

/**
 * 공이 생성될 수 있는 x축의 전체 범위예요.
 * - 공 간 최소 간격(minDistance) * 개수(count)보다 작으면 에러가 발생해요.
 */
const BALL_SPAWN_SPREAD = 24;

/**
 * 공 사이의 최소 간격이에요.
 * - 이보다 가까우면 충돌을 피하기 어려워요.
 */
const BALL_SPAWN_MIN_DISTANCE = 1;

/**
 * 생성할 수 있는 최소 공 개수예요.
 */
const MIN_BALL_COUNT = 2;

/**
 * 생성할 수 있는 최대 공 개수예요.
 */
const MAX_BALL_COUNT = 9;

/**
 * 각 공에 고유한 ID를 부여하기 위한 전역 카운터예요.
 */
let ballIdCounter = 0;

export function useBallState() {
  const [count, setCount] = useState<number>(5);
  const [balls, setBalls] = useState<BallData[]>([]);

  useEffect(() => {
    generateBalls(count);
  }, [count]);

  const removeBall = (id: number) => {
    setBalls((prev) => prev.filter((ball) => ball.id !== id));
  };

  const incrementCount = () => {
    setCount((prev) => Math.min(prev + 1, MAX_BALL_COUNT));
  };

  const decrementCount = () => {
    setCount((prev) => Math.max(prev - 1, MIN_BALL_COUNT));
  };

  const generateBalls = (count: number) => {
    const newBalls = generateRandomPositions({
      count,
      spread: BALL_SPAWN_SPREAD,
      minDistance: BALL_SPAWN_MIN_DISTANCE,
    }).map((position, index) => ({
      id: ++ballIdCounter,
      number: index + 1,
      position,
      mesh: createRef<THREE.Mesh>(),
    }));

    setBalls(newBalls);
  };

  const resetBalls = () => {
    generateBalls(count);
  };

  return {
    balls,
    count,
    setCount,
    incrementCount,
    decrementCount,
    removeBall,
    resetBalls,
  };
}

import { useEffect, useState, useRef, useCallback } from 'react';

interface UseCountdownTimerProps {
  duration: number;
  onCount?: (remainingTime: number) => void;
  onEnd?: () => void;
}

export function useCountdownTimer({
  duration,
  onCount,
  onEnd,
}: UseCountdownTimerProps) {
  const [remainingTime, setRemainingTime] = useState<number>(duration);
  const [isCountingDown, setIsCountingDown] = useState<boolean>(false);
  const intervalRef = useRef<number | null>(null);

  const clear = useCallback(() => {
    if (intervalRef.current !== null) {
      clearInterval(intervalRef.current);
      intervalRef.current = null;
    }
  }, []);

  const resetCountdown = useCallback(() => {
    clear();
    setIsCountingDown(false);
    setRemainingTime(duration);
  }, [clear, duration]);

  const startCountdown = useCallback(() => {
    if (isCountingDown || intervalRef.current !== null) {
      return;
    }

    setIsCountingDown(true);

    intervalRef.current = setInterval(() => {
      setRemainingTime((prev) => {
        const nextTime = prev - 1;

        if (onCount) {
          onCount(nextTime);
        }

        if (nextTime > 0) {
          return nextTime;
        }

        clear();
        setIsCountingDown(false);

        if (onEnd) {
          onEnd();
        }

        return duration;
      });
    }, 1000);
  }, [clear, duration, onCount, onEnd, isCountingDown]);

  useEffect(() => {
    return () => clear();
  }, [clear]);

  return {
    startCountdown,
    resetCountdown,
    remainingTime,
    isCountingDown,
  };
}

import { useGLTF } from '@react-three/drei';
import { useMemo } from 'react';
import * as THREE from 'three';
import { GLTF } from 'three-stdlib';

type GLTFResult = GLTF & {
  nodes: {
    [name: string]: THREE.Object3D<THREE.Object3DEventMap>;
  };
};

export const useGameMapModel = (path: string) => {
  const { nodes } = useGLTF(path) as GLTFResult;

  // 동적으로 모든 노드를 그룹화하고 크기 계산
  const getModelSizeFromAllNodes = (nodes: {
    [name: string]: THREE.Object3D;
  }) => {
    const group = new THREE.Group();
    Object.values(nodes).forEach((node) => {
      if (node instanceof THREE.Object3D === false) {
        return;
      }

      group.add(node.clone());
    });

    const box = new THREE.Box3().setFromObject(group);
    return box.getSize(new THREE.Vector3());
  };

  const size = useMemo(() => getModelSizeFromAllNodes(nodes), [nodes]);

  return {
    nodes,
    size,
  };
};

import { useState } from 'react';

export const GAME_STATUS = {
  READY: 'ready',
  PLAYING: 'playing',
  RANKING: 'ranking',
} as const;

export type GameStatus = (typeof GAME_STATUS)[keyof typeof GAME_STATUS];

interface UseGameStatusProps {
  resetBalls: () => void;
  resetFinishedBalls: () => void;
}

export function useGameStatus({
  resetBalls,
  resetFinishedBalls,
}: UseGameStatusProps) {
  const [gameStatus, setGameStatus] = useState<GameStatus>(GAME_STATUS.READY);

  const isPlaying = gameStatus === GAME_STATUS.PLAYING;

  const startGame = () => {
    setGameStatus(GAME_STATUS.PLAYING);
  };

  const resetGame = () => {
    resetBalls();
    resetFinishedBalls();
    setGameStatus(GAME_STATUS.READY);
  };

  const endGame = () => {
    setGameStatus(GAME_STATUS.RANKING);
  };

  return {
    gameStatus,
    isPlaying,
    startGame,
    endGame,
    resetGame,
  };
}

import { useEffect, useRef } from 'react';
import * as THREE from 'three';

export const useGoalZCalculation = (
  meshRef: React.RefObject<THREE.Group>,
  depth: number,
  onGoalZCalculated: (goalZ: number) => void
) => {
  const worldPosition = useRef(new THREE.Vector3());

  useEffect(() => {
    if (meshRef.current === null) {
      return;
    }

    meshRef.current.getWorldPosition(worldPosition.current);
    const goalZ = worldPosition.current.z + depth / 2;
    onGoalZCalculated(goalZ);
  }, [depth, onGoalZCalculated]);
};

import { startOfWeek } from 'date-fns';
import { useEffect, useMemo, useRef, useState } from 'react';
import { Storage } from '@apps-in-toss/web-framework';

export interface TaskItemType {
  id: string;
  text: string;
  isDone: boolean;
  isLike: boolean;
  date: number;
}

interface Props {
  storageKey: string;
  currentDate: string;
}

export function useTaskState({ storageKey, currentDate }: Props) {
  const weekStartTime = useMemo(() => {
    return startOfWeek(new Date(), { weekStartsOn: 1 }).getTime();
  }, []);

  const taskStoreRef = useRef<Map<string, TaskItemType[]>>(new Map());
  const [tasks, setTasks] = useState<TaskItemType[]>([]);

  useEffect(() => {
    const fetchStorage = async () => {
      try {
        const storageData = await Storage.getItem(storageKey);

        const parsedData: Record<string, TaskItemType[]> = storageData
          ? JSON.parse(storageData)
          : {};
        const filteredMap = Object.entries(parsedData).filter(([date]) => {
          const currentTime = new Date(date).getTime();
          return currentTime >= weekStartTime;
        });
        const map = new Map(filteredMap);
        taskStoreRef.current = map;
        setTasks(taskStoreRef.current.get(currentDate) || []);
      } catch (e) {
        console.error('localStorage parse error:', e);
      }
    };

    fetchStorage();
  }, [storageKey, currentDate, weekStartTime]);

  useEffect(() => {
    taskStoreRef.current.set(currentDate, tasks);

    const persistTaskStore = async () => {
      if (taskStoreRef.current.size === 0) {
        return;
      }

      try {
        const serialized = JSON.stringify(
          Object.fromEntries(taskStoreRef.current)
        );
        await Storage.setItem(storageKey, serialized);
      } catch (e) {
        console.error('Failed to persist tasks to storage:', e);
      }
    };
    persistTaskStore();
  }, [tasks, storageKey, currentDate]);

  const sortTasks = (tasks: TaskItemType[]) =>
    [...tasks].sort(
      (a, b) => Number(b.isLike) - Number(a.isLike) || a.date - b.date
    );

  const addTask = (newTask: TaskItemType) => {
    setTasks((prev) => sortTasks([newTask, ...prev]));
  };

  const deleteTask = (id: string) => {
    setTasks((prev) => prev.filter((task) => task.id !== id));
  };

  const toggleTaskLike = (id: string, isLike: boolean) => {
    setTasks((prev) =>
      sortTasks(
        prev.map((task) => (task.id === id ? { ...task, isLike } : task))
      )
    );
  };

  return {
    tasks,
    setTasks,
    addTask,
    deleteTask,
    toggleTaskLike,
  };
}

import { useState, useCallback } from 'react';
import { fetchAlbumPhotos, ImageResponse } from '@apps-in-toss/framework';
import { usePermissionGate } from './usePermissionGate';
import { useToast } from '@toss/tds-react-native';

export interface ImageState extends ImageResponse {
  previewUri: string;
}

interface UseAlbumPhotosProps {
  base64?: boolean;
}

export function useAlbumPhotos({ base64 = false }: UseAlbumPhotosProps) {
  const [albumPhotos, setAlbumPhotos] = useState<ImageState[]>([]);
  const toast = useToast();
  const permissionGate = usePermissionGate({
    getPermission: () => fetchAlbumPhotos.getPermission(),
    openPermissionDialog: () => fetchAlbumPhotos.openPermissionDialog(),
    onPermissionRequested: (status) => console.log(`권한 요청 결과: ${status}`),
  });

  const loadPhotos = useCallback(async () => {
    try {
      const response = await permissionGate.ensureAndRun(() =>
        fetchAlbumPhotos({ maxWidth: 360, base64 })
      );

      if (!response) {
        return;
      }

      const newImages = response.map((img) => ({
        ...img,
        previewUri: base64
          ? `data:image/jpeg;base64,${img.dataUri}`
          : img.dataUri,
      }));

      setAlbumPhotos((prev) => [...prev, ...newImages]);
    } catch (error) {
      let errorMessage = '앨범을 가져오는 데 실패했어요';

      if (error instanceof Error) {
        errorMessage = error.message;
      }

      toast.open(`${errorMessage}`);
    }
  }, [base64]);

  const deletePhoto = useCallback((id: string) => {
    setAlbumPhotos((prev) => prev.filter((album) => album.id !== id));
  }, []);

  return { albumPhotos, loadPhotos, deletePhoto };
}

import { useCallback, useEffect, useRef, useState } from 'react';
import { PermissionStatus } from '@apps-in-toss/framework';

export interface PermissionGateConfig {
  getPermission: () => Promise<PermissionStatus>;
  openPermissionDialog: () => Promise<PermissionStatus>;
  onPermissionRequested?: (status: PermissionStatus) => void;
}

interface UsePermissionGateReturn {
  permission: PermissionStatus | null;
  isRequestingPermission: boolean;
  isExecuting: boolean;
  ensureAndRun: <T>(fn: () => Promise<T>) => Promise<T | undefined>;
  checkPermission: () => Promise<PermissionStatus>;
  requestPermission: () => Promise<PermissionStatus>;
}

const isAllowed = (status: PermissionStatus) => status === 'allowed';

export function usePermissionGate(
  config: PermissionGateConfig
): UsePermissionGateReturn {
  const [permission, setPermission] = useState<PermissionStatus | null>(null);
  const [isRequestingPermission, setIsRequestingPermission] = useState(false);
  const [isExecuting, setIsExecuting] = useState(false);

  const mountedRef = useRef<boolean>(false);
  const execCountRef = useRef<number>(0);
  const reqCountRef = useRef<number>(0);

  const runIfMounted = useCallback((fn: () => void) => {
    if (mountedRef.current) {
      fn();
    }
  }, []);

  const checkPermission = useCallback(async () => {
    const status = await config.getPermission();
    runIfMounted(() => setPermission(status));
    return status;
  }, [config, runIfMounted]);

  const requestPermission = useCallback(async () => {
    reqCountRef.current += 1;
    runIfMounted(() => setIsRequestingPermission(true));
    try {
      const status = await config.openPermissionDialog();
      runIfMounted(() => setPermission(status));
      config.onPermissionRequested?.(status);
      return status;
    } finally {
      reqCountRef.current -= 1;
      if (reqCountRef.current === 0) {
        runIfMounted(() => setIsRequestingPermission(false));
      }
    }
  }, [config, runIfMounted]);

  const ensureAndRun = useCallback(
    async <T>(fn: () => Promise<T>) => {
      const current = await checkPermission();
      const allowed =
        isAllowed(current) || isAllowed(await requestPermission());

      if (!allowed) {
        const error = new Error('권한을 거절했어요.');
        error.name = 'PermissionDeniedError';
        throw error;
      }

      execCountRef.current += 1;
      runIfMounted(() => setIsExecuting(true));
      try {
        const result = await fn();
        return result;
      } finally {
        execCountRef.current -= 1;

        if (execCountRef.current === 0) {
          runIfMounted(() => setIsExecuting(false));
        }
      }
    },
    [checkPermission, requestPermission, runIfMounted]
  );

  useEffect(() => {
    mountedRef.current = true;
    return () => {
      mountedRef.current = false;
    };
  }, []);

  return {
    permission,
    isRequestingPermission,
    isExecuting,
    ensureAndRun,
    checkPermission,
    requestPermission,
  };
}

import { useState } from 'react';
import { appLogin } from '@apps-in-toss/framework';
import { useToast } from '@toss/tds-react-native';
import { post } from 'utils/fetcher';
import type {
  LoginRequest,
  RefreshTokenRequest,
  TokenResponse,
  LogoutResult,
  AuthState,
  LogoutByAccessTokenRequest,
  LogoutByUserKeyRequest,
} from 'types/auth';

export const useAuth = (): AuthState => {
  const [accessToken, setAccessToken] = useState<string | null>(null);
  const [refreshToken, setRefreshToken] = useState<string | null>(null);
  const [loading, setLoading] = useState<boolean>(false);
  const [error, setError] = useState<string | null>(null);
  const toast = useToast();

  const clearTokens = () => {
    setAccessToken(null);
    setRefreshToken(null);
  };

  const login = async () => {
    try {
      setLoading(true);
      setError(null);

      const { authorizationCode, referrer } = await appLogin();

      const data = await post<TokenResponse, LoginRequest>(
        '/get-access-token',
        { authorizationCode, referrer }
      );

      const access = data?.data?.success?.accessToken;
      const refresh = data?.data?.success?.refreshToken;

      if (access) {
        setAccessToken(access);
        toast.open('로그인 성공, AccessToken을 가져왔어요.');
      } else {
        setError('AccessToken을 가져오지 못했어요.');
      }

      if (refresh) {
        setRefreshToken(refresh);
      }
    } catch (e) {
      console.error(e);
      setError('AccessToken을 가져오는 중 문제가 발생했어요.');
    } finally {
      setLoading(false);
    }
  };

  const refreshAccessToken = async () => {
    if (!refreshToken) {
      setError('RefreshToken이 없어요. 먼저 로그인 해주세요.');
      return;
    }

    try {
      setLoading(true);
      setError(null);

      const data = await post<TokenResponse, RefreshTokenRequest>(
        '/refresh-token',
        { refreshToken }
      );

      const access = data?.data?.success?.accessToken;

      if (access) {
        setAccessToken(access);
        toast.open('재인증 성공, AccessToken을 다시 가져왔어요.');
      } else {
        setError('AccessToken 재발급에 실패했어요.');
      }
    } catch (e) {
      console.error(e);
      setError('AccessToken 재발급 중 문제가 발생했어요.');
    } finally {
      setLoading(false);
    }
  };

  const logoutByAccessToken = async (): Promise<LogoutResult> => {
    if (!accessToken) {
      setError('AccessToken이 없어요. 먼저 로그인 해주세요.');
      return null;
    }

    try {
      setLoading(true);
      setError(null);

      await post<unknown, LogoutByAccessTokenRequest>(
        '/logout-by-access-token',
        {},
        accessToken
      );

      clearTokens();
      return { ok: true };
    } catch (error) {
      console.error(error);
      setError('AccessToken으로 로그아웃하는 중 문제가 발생했어요.');
      return null;
    } finally {
      setLoading(false);
    }
  };

  const logoutByUserKey = async (userKey: string): Promise<LogoutResult> => {
    if (!userKey) {
      setError('userKey가 필요해요.');
      return null;
    }

    try {
      setLoading(true);
      setError(null);

      await post<unknown, LogoutByUserKeyRequest>('/logout-by-user-key', {
        userKey,
      });

      clearTokens();
      return { ok: true };
    } catch (error) {
      console.error(error);
      setError('userKey로 로그아웃하는 중 문제가 발생했어요.');
      return null;
    } finally {
      setLoading(false);
    }
  };

  return {
    loading,
    error,
    accessToken,
    refreshToken,
    login,
    refreshAccessToken,
    logoutByAccessToken,
    logoutByUserKey,
  };
};

import { useState } from 'react';
import { useToast } from '@toss/tds-react-native';
import { get } from 'utils/fetcher';
import type { GetUserInfoResponse, UserInfo, UserInfoState } from 'types/user';

export const useUserInfo = (): UserInfoState => {
  const [userInfo, setUserInfo] = useState<UserInfo | null>(null);
  const [loading, setLoading] = useState<boolean>(false);
  const [error, setError] = useState<string | null>(null);
  const toast = useToast();

  const getUserInfo = async (accessToken: string) => {
    if (!accessToken) {
      setError('AccessToken이 필요해요.');
      return;
    }

    try {
      setLoading(true);
      setError(null);

      const data = await get<GetUserInfoResponse>(
        '/get-user-info',
        accessToken
      );
      const parsed = data?.data?.success;

      if (parsed) {
        setUserInfo(parsed);
        const keys = Object.entries(parsed)
          .filter(([_, value]) => value !== null)
          .map(([key]) => key);
        toast.open(`가져온 유저 정보, ${keys.join(', ')}`);
      } else {
        setError('유저 정보를 불러오지 못했어요.');
      }
    } catch (error) {
      console.error(error);
      setError('유저 정보를 가져오는 중 문제가 발생했어요.');
    } finally {
      setLoading(false);
    }
  };

  const clearUserInfo = () => {
    setUserInfo(null);
  };

  return {
    userInfo,
    loading,
    error,
    getUserInfo,
    clearUserInfo,
  };
};

import { useCallback, useState } from "react";
import { ImageResponse, openCamera } from "@apps-in-toss/framework";
import { usePermissionGate } from "hooks/usePermissionGate";
import { useToast } from "@toss/tds-react-native";

export interface ImageState extends ImageResponse {
  previewUri: string;
}

interface UseCameraProps {
  base64?: boolean;
}

export function useCamera({ base64 = false }: UseCameraProps) {
  const [image, setImage] = useState<ImageState | null>(null);
  const toast = useToast();
  const permissionGate = usePermissionGate({
    getPermission: () => openCamera.getPermission(),
    openPermissionDialog: () => openCamera.openPermissionDialog(),
    onPermissionRequested: (status) => console.log(`권한 요청 결과: ${status}`),
  });

  const capturePhoto = useCallback(async () => {
    try {
      const response = await permissionGate.ensureAndRun(() =>
        openCamera({ base64 })
      );

      if (!response) {
        return;
      }

      const newImage: ImageState = {
        ...response,
        previewUri: base64
          ? `data:image/jpeg;base64,${response.dataUri}`
          : response.dataUri,
      };

      setImage(newImage);
    } catch (error) {
      let errorMessage = "카메라를 불러오는 데 실패했어요.";

      if (error instanceof Error) {
        errorMessage = error.message;
      }

      toast.open(`${errorMessage}`);
    }
  }, [base64, permissionGate]);

  const clearPhoto = useCallback(() => {
    setImage(null);
  }, []);

  return { image, capturePhoto, clearPhoto };
}

import { useCallback, useEffect, useRef, useState } from 'react';
import { PermissionStatus } from '@apps-in-toss/framework';

export interface PermissionGateConfig {
  getPermission: () => Promise<PermissionStatus>;
  openPermissionDialog: () => Promise<PermissionStatus>;
  onPermissionRequested?: (status: PermissionStatus) => void;
}

interface UsePermissionGateReturn {
  permission: PermissionStatus | null;
  isRequestingPermission: boolean;
  isExecuting: boolean;
  ensureAndRun: <T>(fn: () => Promise<T>) => Promise<T | undefined>;
  checkPermission: () => Promise<PermissionStatus>;
  requestPermission: () => Promise<PermissionStatus>;
}

const isAllowed = (status: PermissionStatus) => status === 'allowed';

export function usePermissionGate(
  config: PermissionGateConfig
): UsePermissionGateReturn {
  const [permission, setPermission] = useState<PermissionStatus | null>(null);
  const [isRequestingPermission, setIsRequestingPermission] = useState(false);
  const [isExecuting, setIsExecuting] = useState(false);

  const mountedRef = useRef<boolean>(false);
  const execCountRef = useRef<number>(0);
  const reqCountRef = useRef<number>(0);

  const runIfMounted = useCallback((fn: () => void) => {
    if (mountedRef.current) {
      fn();
    }
  }, []);

  const checkPermission = useCallback(async () => {
    const status = await config.getPermission();
    runIfMounted(() => setPermission(status));
    return status;
  }, [config, runIfMounted]);

  const requestPermission = useCallback(async () => {
    reqCountRef.current += 1;
    runIfMounted(() => setIsRequestingPermission(true));
    try {
      const status = await config.openPermissionDialog();
      runIfMounted(() => setPermission(status));
      config.onPermissionRequested?.(status);
      return status;
    } finally {
      reqCountRef.current -= 1;
      if (reqCountRef.current === 0) {
        runIfMounted(() => setIsRequestingPermission(false));
      }
    }
  }, [config, runIfMounted]);

  const ensureAndRun = useCallback(
    async <T>(fn: () => Promise<T>) => {
      const current = await checkPermission();
      const allowed =
        isAllowed(current) || isAllowed(await requestPermission());

      if (!allowed) {
        const error = new Error('권한을 거절했어요.');
        error.name = 'PermissionDeniedError';
        throw error;
      }

      execCountRef.current += 1;
      runIfMounted(() => setIsExecuting(true));
      try {
        const result = await fn();
        return result;
      } finally {
        execCountRef.current -= 1;

        if (execCountRef.current === 0) {
          runIfMounted(() => setIsExecuting(false));
        }
      }
    },
    [checkPermission, requestPermission, runIfMounted]
  );

  useEffect(() => {
    mountedRef.current = true;
    return () => {
      mountedRef.current = false;
    };
  }, []);

  return {
    permission,
    isRequestingPermission,
    isExecuting,
    ensureAndRun,
    checkPermission,
    requestPermission,
  };
}

