import { useCallback, useEffect, useRef, useMemo } from 'react';
import { Howl } from 'howler';
import {
  clampVolume,
  resumeHowlerContext,
  createHowlConfig,
  isAudioPlaying,
  setAudioVolume,
} from '@/utils/audioUtils';
import { useVisibilityChange } from '@/hooks/useVisibilityChange';

interface AudioProps {
  src: string;
  loop?: boolean;
  volume?: number;
}

interface AudioControls {
  playAudio: () => Promise<void>;
  stopAudio: () => void;
  pause: () => void;
  fade: (from: number, to: number, duration: number) => void;
  audioRef: React.RefObject<Howl | null>;
}

export function useAudio({
  src,
  loop = false,
  volume = 1,
}: AudioProps): AudioControls {
  const howlRef = useRef<Howl | null>(null);
  const wasPlayingRef = useRef<boolean>(false);
  const isInitializedRef = useRef<boolean>(false);

  // Howl 설정을 메모이제이션하여 불필요한 재생성 방지 (volume은 런타임에 별도 동기화)
  const howlConfig = useMemo(() => createHowlConfig(src, loop, 0), [src, loop]);

  // Howl 인스턴스 초기화
  useEffect(() => {
    if (isInitializedRef.current) {
      return;
    }

    const howl = new Howl(howlConfig);
    howlRef.current = howl;
    isInitializedRef.current = true;

    return () => {
      if (howlRef.current) {
        howlRef.current.stop();
        howlRef.current.unload();
        howlRef.current = null;
      }
      isInitializedRef.current = false;
    };
  }, [howlConfig]);

  // 볼륨 변경 시 업데이트
  useEffect(() => {
    setAudioVolume(howlRef.current, volume);
  }, [volume]);

  const playAudio = useCallback(async (): Promise<void> => {
    const howl = howlRef.current;
    if (howl === null) {
      console.warn('오디오 인스턴스가 초기화되지 않았어요.');
      return;
    }

    try {
      setAudioVolume(howl, volume);

      howl.play();
      wasPlayingRef.current = true;
    } catch (error) {
      console.warn('Howl 오디오 재생 실패:', error);
    }
  }, [volume]);

  const stopAudio = useCallback((): void => {
    const howl = howlRef.current;
    if (howl === null) {
      return;
    }

    howl.stop();
    wasPlayingRef.current = false;
  }, []);

  const pause = useCallback((): void => {
    const howl = howlRef.current;
    if (howl === null) {
      return;
    }

    howl.pause();
    wasPlayingRef.current = false;
  }, []);

  const fade = useCallback(
    (from: number, to: number, duration: number): void => {
      const howl = howlRef.current;
      if (howl === null) {
        return;
      }

      howl.fade(clampVolume(from), clampVolume(to), duration);
    },
    []
  );

  // iOS 오디오 세션 관리를 위한 가시성 변경 처리
  const handleVisible = useCallback(() => {
    const howl = howlRef.current;
    if (howl === null) {
      return;
    }

    // iOS: 이전에 재생 중이었다면 오디오 세션 재개 및 재생
    if (wasPlayingRef.current === false) {
      return;
    }

    // 오디오 컨텍스트가 준비될 때까지 짧은 지연
    setTimeout(() => {
      try {
        resumeHowlerContext();
        howl.play();
      } catch (error) {
        console.warn('오디오 재개 실패:', error);
        // 재개 실패 시 unlock 이벤트를 통해 재생 재시도
        howl.once('unlock', () => {
          howl.play();
        });
      }
    }, 100);
  }, []);

  const handleHidden = useCallback(() => {
    const howl = howlRef.current;
    if (howl === null) {
      return;
    }

    // 백그라운드로 가기 전 재생 상태 기억
    wasPlayingRef.current = isAudioPlaying(howl);
    howl.pause();
  }, []);

  useVisibilityChange({
    onVisible: handleVisible,
    onHidden: handleHidden,
  });

  return {
    audioRef: howlRef,
    playAudio,
    stopAudio,
    pause,
    fade,
  };
}

import { useCallback, useEffect, useMemo, useRef, useState } from 'react';
import { GAME_MODE, GAME_STATE } from '@/constants/game';
import {
  buildBoard,
  calculateStats,
  checkWinCondition,
  floodFill,
  type BoardCell,
} from '@/utils/gameLogic';

export function useBoardManager(gameConfig: {
  rows: number;
  cols: number;
  minMines: number;
  maxMines: number;
}) {
  const [lives, setLives] = useState<number>(2);
  const [board, setBoard] = useState<BoardCell[][]>(() =>
    buildBoard(
      gameConfig.rows,
      gameConfig.cols,
      gameConfig.minMines,
      gameConfig.maxMines
    )
  );
  const [mode, setMode] = useState<(typeof GAME_MODE)[keyof typeof GAME_MODE]>(
    GAME_MODE.REVEAL
  );
  const [state, setState] = useState<
    (typeof GAME_STATE)[keyof typeof GAME_STATE]
  >(GAME_STATE.PLAYING);
  const [version, setVersion] = useState(0);

  const stats = useMemo(() => calculateStats(board), [board]);

  const isFirstRender = useRef(true);
  useEffect(() => {
    if (isFirstRender.current) {
      isFirstRender.current = false;
      return;
    }

    setBoard(
      buildBoard(
        gameConfig.rows,
        gameConfig.cols,
        gameConfig.minMines,
        gameConfig.maxMines
      )
    );
    setMode(GAME_MODE.REVEAL);
    setState(GAME_STATE.PLAYING);
    setVersion((v) => v + 1);
  }, [gameConfig]);

  const toggleMode = useCallback(
    () =>
      setMode((m) =>
        m === GAME_MODE.REVEAL ? GAME_MODE.FLAG : GAME_MODE.REVEAL
      ),
    []
  );

  const revealAllMines = useCallback(() => {
    setBoard((prev) =>
      prev.map((row) =>
        row.map((cell) => (cell.isMine ? { ...cell, isRevealed: true } : cell))
      )
    );
  }, []);

  const checkWin = useCallback(
    (nextBoard: BoardCell[][]) => {
      if (checkWinCondition(nextBoard)) {
        setState(GAME_STATE.WON);
        setTimeout(() => revealAllMines(), 0);
      }
    },
    [revealAllMines]
  );

  const handleCellClick = useCallback(
    (cell: BoardCell) => {
      if (state !== GAME_STATE.PLAYING) {
        return;
      }
      if (cell.isRevealed) {
        return;
      }

      const isSameCell = (c: BoardCell) =>
        c.row === cell.row && c.col === cell.col && c.isRevealed === false;

      if (mode === GAME_MODE.FLAG) {
        setBoard((prev) =>
          prev.map((row) =>
            row.map((c) =>
              isSameCell(c) ? { ...c, isFlagged: !c.isFlagged } : c
            )
          )
        );
        return;
      }

      if (cell.isFlagged) {
        return;
      }

      if (cell.isMine) {
        setState(GAME_STATE.LOST);
        setLives((prev) => prev - 1);
        revealAllMines();
        return;
      }

      setBoard((prev) => {
        const next = prev.map((row) => row.map((c) => ({ ...c })));
        floodFill(next, cell.row, cell.col, gameConfig.rows, gameConfig.cols);
        checkWin(next);
        return next;
      });
    },
    [checkWin, mode, revealAllMines, state, gameConfig]
  );

  const reset = useCallback(() => {
    if (lives === 0) {
      return;
    }
    setBoard(
      buildBoard(
        gameConfig.rows,
        gameConfig.cols,
        gameConfig.minMines,
        gameConfig.maxMines
      )
    );
    setMode(GAME_MODE.REVEAL);
    setState(GAME_STATE.PLAYING);
    setVersion((v) => v + 1);
  }, [gameConfig, lives]);

  const addLife = useCallback((amount?: number) => {
    const newLife = amount || 1;
    setLives((prev) => prev + newLife);
  }, []);

  return {
    lives,
    board,
    mode,
    state,
    stats,
    version,
    handlers: {
      reset,
      toggleMode,
      handleCellClick,
      addLife,
    },
  } as const;
}

import { useMemo, useState } from 'react';
import {
  BOARD_SIZE,
  DIFFICULTY_DENSITY,
  DEFAULT_DIFFICULTY,
} from '@/constants/game';

export interface GameConfig {
  rows: number;
  cols: number;
  minMines: number;
  maxMines: number;
  total: number;
}

export function useDifficultyConfig() {
  const [difficulty, setDifficulty] =
    useState<keyof typeof DIFFICULTY_DENSITY>(DEFAULT_DIFFICULTY);

  const gameConfig: GameConfig = useMemo(() => {
    const { ROWS: rows, COLS: cols } = BOARD_SIZE;
    const total = rows * cols;
    const [minPct, maxPct] = DIFFICULTY_DENSITY[difficulty];
    const minMines = Math.max(1, Math.floor(total * minPct));
    const maxMines = Math.min(
      total - 1,
      Math.max(minMines + 1, Math.floor(total * maxPct))
    );

    return { rows, cols, minMines, maxMines, total };
  }, [difficulty]);

  return {
    difficulty,
    setDifficulty,
    gameConfig,
  } as const;
}

import { useCallback, useEffect, useRef, useState } from 'react';
import { createPortal } from 'react-dom';

export function useHeartOverlay() {
  const [visible, setVisible] = useState(false);
  const [rev, setRev] = useState(0);
  const overlayElementRef = useRef<HTMLElement | null>(
    document.getElementById('overlay')
  );
  const hideTimerRef = useRef<number | null>(null);

  const hide = useCallback(() => {
    setVisible(false);
  }, []);

  const play = useCallback(() => {
    // 대기 중인 자동 숨김 제거
    if (hideTimerRef.current) {
      window.clearTimeout(hideTimerRef.current);
      hideTimerRef.current = null;
    }

    // 이미 보이고 있으면 잠시 언마운트 후 다시 마운트하여 재생
    if (visible) {
      setVisible(false);
      requestAnimationFrame(() => {
        setRev((v) => v + 1);
        setVisible(true);
      });
      return;
    }

    setRev((v) => v + 1);
    setVisible(true);
  }, [visible]);

  // 선택적: APNG가 필요한 경우 자동 숨김
  useEffect(() => {
    if (!visible) return;
    if (hideTimerRef.current) {
      window.clearTimeout(hideTimerRef.current);
    }
    // APNG는 종료 이벤트가 없어 필요 시 타이머로 제어
    // 필요 없으면 이 타이머를 제거하거나 시간값을 조절하세요
    hideTimerRef.current = window.setTimeout(() => {
      setVisible(false);
    }, 900);

    return () => {
      if (hideTimerRef.current) {
        window.clearTimeout(hideTimerRef.current);
      }
    };
  }, [visible]);

  const Portal = useCallback(() => {
    const overlayElement = overlayElementRef.current;
    const src = `/heart-animation.png?v=${rev}`;

    if (visible === false || overlayElement === null) {
      return null;
    }

    return createPortal(
      <div className="heart-container">
        <img src={src} className="heart" alt="" />
      </div>,
      overlayElement
    );
  }, [visible, rev]);

  return { play, hide, Portal } as const;
}

import { useEffect, useCallback } from 'react';

/**
 * 가시성 변경 이벤트를 처리하는 커스텀 훅
 * @param onVisible 앱이 포그라운드로 돌아올 때 실행할 콜백
 * @param onHidden 앱이 백그라운드로 갈 때 실행할 콜백
 */
interface UseVisibilityChangeOptions {
  onVisible?: () => void;
  onHidden?: () => void;
}

export interface VisibilityChangeAPI {
  onVisible?: () => void;
  onHidden?: () => void;
  waitForVisibility: (
    targetState?: DocumentVisibilityState,
    delayMs?: number
  ) => Promise<void>;
}

export const useVisibilityChange = (
  options?: UseVisibilityChangeOptions
): VisibilityChangeAPI => {
  const { onVisible, onHidden } = options ?? {};
  const handleVisibilityChange = useCallback(() => {
    if (document.visibilityState === 'visible') {
      onVisible?.();
    } else {
      onHidden?.();
    }
  }, [onVisible, onHidden]);

  useEffect(() => {
    window.addEventListener('visibilitychange', handleVisibilityChange);

    return () => {
      window.removeEventListener('visibilitychange', handleVisibilityChange);
    };
  }, [handleVisibilityChange]);

  return { onVisible, onHidden, waitForVisibility };
};

/**
 * 특정 가시성 상태가 될 때까지 대기하는 유틸 함수
 * 기존 훅 동작에 영향을 주지 않는 순수 함수입니다.
 */
function resolveWithDelay(resolve: () => void, delayMs: number): void {
  if (delayMs > 0) {
    setTimeout(resolve, delayMs);
  } else {
    resolve();
  }
}

function waitForVisibility(
  targetState: DocumentVisibilityState = 'visible',
  delayMs: number = 0
): Promise<void> {
  if (document.visibilityState === targetState) {
    return new Promise((resolve) => resolveWithDelay(resolve, delayMs));
  }

  return new Promise((resolve) => {
    const handler = () => {
      if (document.visibilityState === targetState) {
        window.removeEventListener('visibilitychange', handler);
        resolveWithDelay(resolve, delayMs);
      }
    };

    window.addEventListener('visibilitychange', handler, { once: false });
  });
}

import { useCallback, useState } from "react";
import { ContactEntity, fetchContacts } from "@apps-in-toss/framework";
import { usePermissionGate } from "./usePermissionGate";
import { useToast } from "@toss/tds-react-native";

interface ContactsResponse {
  result: ContactEntity[];
  nextOffset: number | null;
  done: boolean;
}

export function useContacts() {
  const [contacts, setContacts] = useState<ContactsResponse>({
    result: [],
    nextOffset: 0,
    done: false,
  });
  const toast = useToast();
  const permissionGate = usePermissionGate({
    getPermission: () => fetchContacts.getPermission(),
    openPermissionDialog: () => fetchContacts.openPermissionDialog(),
    onPermissionRequested: (status) => console.log(`권한 요청 결과: ${status}`),
  });

  const loadContacts = useCallback(async () => {
    try {
      if (contacts.done) {
        return;
      }

      const response = await permissionGate.ensureAndRun(() =>
        fetchContacts({
          size: 10,
          offset: contacts.nextOffset ?? 0,
        })
      );

      if (!response) {
        return;
      }

      setContacts((prev) => ({
        result: [...prev.result, ...response.result],
        nextOffset: response.nextOffset,
        done: response.done,
      }));
    } catch (error) {
      let errorMessage = "연락처를 가져오는 데 실패했어요";

      if (error instanceof Error) {
        errorMessage = error.message;
      }

      toast.open(`${errorMessage}`);
    }
  }, [contacts.done, contacts.nextOffset]);

  return {
    contacts: contacts.result,
    done: contacts.done,
    permission: permissionGate.permission,
    reloadContacts: loadContacts,
  };
}

import { useCallback, useEffect, useRef, useState } from 'react';
import { PermissionStatus } from '@apps-in-toss/framework';

export interface PermissionGateConfig {
  getPermission: () => Promise<PermissionStatus>;
  openPermissionDialog: () => Promise<PermissionStatus>;
  onPermissionRequested?: (status: PermissionStatus) => void;
}

interface UsePermissionGateReturn {
  permission: PermissionStatus | null;
  isRequestingPermission: boolean;
  isExecuting: boolean;
  ensureAndRun: <T>(fn: () => Promise<T>) => Promise<T | undefined>;
  checkPermission: () => Promise<PermissionStatus>;
  requestPermission: () => Promise<PermissionStatus>;
}

const isAllowed = (status: PermissionStatus) => status === 'allowed';

export function usePermissionGate(
  config: PermissionGateConfig
): UsePermissionGateReturn {
  const [permission, setPermission] = useState<PermissionStatus | null>(null);
  const [isRequestingPermission, setIsRequestingPermission] = useState(false);
  const [isExecuting, setIsExecuting] = useState(false);

  const mountedRef = useRef<boolean>(false);
  const execCountRef = useRef<number>(0);
  const reqCountRef = useRef<number>(0);

  const runIfMounted = useCallback((fn: () => void) => {
    if (mountedRef.current) {
      fn();
    }
  }, []);

  const checkPermission = useCallback(async () => {
    const status = await config.getPermission();
    runIfMounted(() => setPermission(status));
    return status;
  }, [config, runIfMounted]);

  const requestPermission = useCallback(async () => {
    reqCountRef.current += 1;
    runIfMounted(() => setIsRequestingPermission(true));
    try {
      const status = await config.openPermissionDialog();
      runIfMounted(() => setPermission(status));
      config.onPermissionRequested?.(status);
      return status;
    } finally {
      reqCountRef.current -= 1;
      if (reqCountRef.current === 0) {
        runIfMounted(() => setIsRequestingPermission(false));
      }
    }
  }, [config, runIfMounted]);

  const ensureAndRun = useCallback(
    async <T>(fn: () => Promise<T>) => {
      const current = await checkPermission();
      const allowed =
        isAllowed(current) || isAllowed(await requestPermission());

      if (!allowed) {
        const error = new Error('권한을 거절했어요.');
        error.name = 'PermissionDeniedError';
        throw error;
      }

      execCountRef.current += 1;
      runIfMounted(() => setIsExecuting(true));
      try {
        const result = await fn();
        return result;
      } finally {
        execCountRef.current -= 1;

        if (execCountRef.current === 0) {
          runIfMounted(() => setIsExecuting(false));
        }
      }
    },
    [checkPermission, requestPermission, runIfMounted]
  );

  useEffect(() => {
    mountedRef.current = true;
    return () => {
      mountedRef.current = false;
    };
  }, []);

  return {
    permission,
    isRequestingPermission,
    isExecuting,
    ensureAndRun,
    checkPermission,
    requestPermission,
  };
}

