import { useCallback, useEffect, useState } from 'react';
import {
  getOperationalEnvironment,
  submitGameCenterLeaderBoardScore,
} from '@apps-in-toss/web-framework';

interface GameCenterScoreProps {
  score: number;
  isGameEnd: boolean;
}

interface GameCenterScoreResult {
  loading: boolean;
}

export const useGameCenterScore = ({
  score,
  isGameEnd,
}: GameCenterScoreProps): GameCenterScoreResult => {
  const [loading, setLoading] = useState<boolean>(false);
  const operationalEnvironment = getOperationalEnvironment();

  const submitScore = useCallback(async () => {
    if (loading) {
      return;
    }

    setLoading(true);

    try {
      const result = await submitGameCenterLeaderBoardScore({
        score: score.toFixed(1),
      });

      if (!result) {
        alert('지원하지 않는 앱 버전이에요.');
        return;
      }

      if (result.statusCode !== 'SUCCESS') {
        alert(`점수 제출 실패: ${result.statusCode}`);
      }
    } catch (error) {
      alert(`점수 제출 중 오류가 발생했어요. ${error}`);
    } finally {
      setLoading(false);
    }
  }, [score]);

  useEffect(() => {
    const isDrop = score === -Infinity;
    const shouldSubmitScore =
      isGameEnd &&
      isDrop === false &&
      loading === false &&
      operationalEnvironment === 'toss';

    if (shouldSubmitScore) {
      submitScore();
    }
  }, [isGameEnd, submitScore]);

  return { loading };
};

import { useState, useEffect, useRef } from 'react';
import { GAME_CONFIG } from '@/utils/gameConfig';

/**
 * useGauge Hook
 * @param isCharging 게이지 활성화 여부
 * @param maxForce 게이지의 최대값
 * @param speed - (선택 사항) 게이지 값이 초당 움직이는 속도 (기본값: maxForce)
 */
export const useGauge = (
  isCharging: boolean,
  maxForce: number,
  speed: number = GAME_CONFIG.FORCE.GAUGE_SPEED
) => {
  const [value, setValue] = useState<number>(GAME_CONFIG.FORCE.MIN);
  const frameIdRef = useRef<number>(0);
  const direction = useRef<number>(1);
  const lastTime = useRef<number>(0);

  useEffect(() => {
    if (isCharging === false) {
      if (frameIdRef.current) {
        cancelAnimationFrame(frameIdRef.current);
      }
      setValue(GAME_CONFIG.FORCE.MIN);
      direction.current = 1;
      lastTime.current = 0;
      return;
    }

    const animate = (timestamp: number) => {
      if (lastTime.current === 0) {
        lastTime.current = timestamp;
      }
      const deltaTime = (timestamp - lastTime.current) / 1000;
      lastTime.current = timestamp;

      setValue((prevValue) => {
        let newValue = prevValue + direction.current * speed * deltaTime;

        if (newValue >= maxForce) {
          newValue = maxForce;
          direction.current = -1;
        } else if (newValue <= GAME_CONFIG.FORCE.MIN) {
          newValue = GAME_CONFIG.FORCE.MIN;
          direction.current = 1;
        }

        return newValue;
      });

      frameIdRef.current = requestAnimationFrame(animate);
    };

    frameIdRef.current = requestAnimationFrame(animate);

    return () => {
      if (frameIdRef.current) {
        cancelAnimationFrame(frameIdRef.current);
      }
    };
  }, [isCharging, maxForce, speed]);

  const normalizedValue =
    maxForce > GAME_CONFIG.FORCE.MIN
      ? (value - GAME_CONFIG.FORCE.MIN) / (maxForce - GAME_CONFIG.FORCE.MIN)
      : 0;

  const fillPercentage = Math.max(0, Math.min(1, normalizedValue));

  return {
    value,
    fillPercentage,
  };
};

import { useEffect, useCallback } from 'react';

/**
 * 가시성 변경 이벤트를 처리하는 커스텀 훅
 * @param onVisible 앱이 포그라운드로 돌아올 때 실행할 콜백
 * @param onHidden 앱이 백그라운드로 갈 때 실행할 콜백
 */
interface UseVisibilityChangeOptions {
  onVisible?: () => void;
  onHidden?: () => void;
}

export const useVisibilityChange = ({
  onVisible,
  onHidden,
}: UseVisibilityChangeOptions) => {
  const handleVisibilityChange = useCallback(() => {
    if (document.visibilityState === 'visible') {
      onVisible?.();
    } else {
      onHidden?.();
    }
  }, [onVisible, onHidden]);

  useEffect(() => {
    window.addEventListener('visibilitychange', handleVisibilityChange);

    return () => {
      window.removeEventListener('visibilitychange', handleVisibilityChange);
    };
  }, [handleVisibilityChange]);
};

