import { useCallback, useRef, useState } from 'react';
import { useFocusEffect } from '@granite-js/native/@react-navigation/native';
import { GoogleAdMob } from '@apps-in-toss/framework';

const TEST_AD_GROUP_ID = 'ait-ad-test-interstitial-id';

interface InterstitialAdCallback {
  onDismiss?: () => void;
}

export function useInterstitialAd() {
  const [loading, setLoading] = useState(true);
  const dismissCallbackRef = useRef<(() => void) | undefined>();

  useFocusEffect(
    useCallback(() => {
      setLoading(true);

      const isAdUnsupported =
        GoogleAdMob.loadAppsInTossAdMob.isSupported?.() === false;

      if (isAdUnsupported) {
        console.warn('광고가 준비되지 않았거나, 지원되지 않아요.');
        return;
      }

      const cleanup = GoogleAdMob.loadAppsInTossAdMob({
        options: {
          adGroupId: TEST_AD_GROUP_ID,
        },
        onEvent: (event) => {
          if (event.type === 'loaded') {
            setLoading(false);
          }
        },
        onError: (error) => {
          console.error('광고 로드 실패', error);
        },
      });

      return cleanup;
    }, [])
  );

  const showInterstitialAd = ({ onDismiss }: InterstitialAdCallback) => {
    const isAdUnsupported =
      GoogleAdMob.showAppsInTossAdMob.isSupported?.() === false;

    if (loading || isAdUnsupported) {
      console.warn('광고가 준비되지 않았거나, 지원되지 않아요.');
      return;
    }

    dismissCallbackRef.current = onDismiss;

    GoogleAdMob.showAppsInTossAdMob({
      options: {
        adGroupId: TEST_AD_GROUP_ID,
      },
      onEvent: (event) => {
        switch (event.type) {
          case 'requested':
            setLoading(false);
            break;

          case 'clicked':
            // 사용자가 광고를 클릭했어요.
            break;

          case 'dismissed':
            dismissCallbackRef.current?.();
            dismissCallbackRef.current = undefined;

            break;

          case 'failedToShow':
            // 광고를 보여주지 못했어요.
            break;

          case 'impression':
            // 광고가 화면에 노출됐어요.
            break;

          case 'show':
            // 광고 컨텐츠가 노출되기 시작했어요.
            break;
        }
      },
      onError: (error) => {
        console.error('광고 보여주기 실패', error);
      },
    });
  };

  return {
    loading,
    showInterstitialAd,
  };
}

import { useRef, useCallback, useState, useEffect } from 'react';
import {
  Accuracy,
  Location,
  startUpdateLocation,
} from '@apps-in-toss/framework';
import { useToast } from '@toss/tds-react-native';
import { getDistanceFromCurrentLocation } from 'utils/locationDistance';
import { usePermissionGate } from './usePermissionGate';

/**
 * 위치 관련 액션에 대해 정의한 사용자 메시지예요.
 * 현재 위치 저장, 추적 중지, 오류 발생 시 사용자에게 보여줄 메시지를 담고 있어요.
 */
const LOCATION_ACTIONS = {
  SAVED_LOCATION: '현재 위치를 기억했어요.',
  STOP_TRACKING: '위치 관찰을 중지했어요.',
  ERROR_LOCATION: '위치 정보를 가져오는데 실패했어요.',
} as const;

interface UseLocationWatcherProps {
  onDistanceInitial?: (initialLocation: Location) => void;
  onDistanceUpdate?: (distance: number) => void;
  onReset?: () => void;
}

export function useLocationWatcher({
  onDistanceInitial,
  onDistanceUpdate,
  onReset,
}: UseLocationWatcherProps) {
  const targetLocationRef = useRef<Location | null>(null);
  const unsubscribeRef = useRef<(() => void) | null>(null);
  const [currentDistance, setCurrentDistance] = useState(0);
  const [error, setError] = useState<string | null>(null);
  const toast = useToast();
  const permissionGate = usePermissionGate({
    getPermission: () => startUpdateLocation.getPermission(),
    openPermissionDialog: () => startUpdateLocation.openPermissionDialog(),
    onPermissionRequested: (status) => console.log(`권한 요청 결과: ${status}`),
  });

  const handleLocationUpdate = useCallback(
    (location: Location) => {
      if (targetLocationRef.current == null) {
        targetLocationRef.current = location;
        toast.open(LOCATION_ACTIONS.SAVED_LOCATION);

        if (onDistanceInitial != null) {
          onDistanceInitial(location);
        }
        return;
      }

      const distance = getDistanceFromCurrentLocation({
        current: {
          latitude: location.coords.latitude,
          longitude: location.coords.longitude,
        },
        target: {
          latitude: targetLocationRef.current.coords.latitude,
          longitude: targetLocationRef.current.coords.longitude,
        },
      });

      setCurrentDistance(distance);

      if (onDistanceUpdate != null) {
        onDistanceUpdate(distance);
      }
    },
    [onDistanceInitial, onDistanceUpdate]
  );

  const handleLocationError = useCallback(() => {
    toast.open(LOCATION_ACTIONS.ERROR_LOCATION);
  }, []);

  const startWatchingLocation = useCallback(async () => {
    if (unsubscribeRef.current != null) {
      return;
    }

    try {
      setError(null);
      const unsubscribe = await permissionGate.ensureAndRun(async () =>
        startUpdateLocation({
          options: {
            accuracy: Accuracy.BestForNavigation,
            timeInterval: 1000,
            distanceInterval: 0.1,
          },
          onEvent: handleLocationUpdate,
          onError: handleLocationError,
        })
      );

      if (unsubscribe) {
        unsubscribeRef.current = unsubscribe;
      }
    } catch (error) {
      let errorMessage = '위치 정보를 가져오는 데 실패했어요.';

      if (error instanceof Error) {
        errorMessage = error.message;
      }

      setError(errorMessage);
    }
  }, [handleLocationUpdate, handleLocationError]);

  const stopWatchingLocation = useCallback(() => {
    if (unsubscribeRef.current != null) {
      unsubscribeRef.current();
      unsubscribeRef.current = null;
    }
    toast.open(LOCATION_ACTIONS.STOP_TRACKING);
    targetLocationRef.current = null;
    setCurrentDistance(0);

    if (onReset != null) {
      onReset();
    }
  }, [onReset]);

  useEffect(() => {
    return () => {
      if (unsubscribeRef.current != null) {
        unsubscribeRef.current();
        unsubscribeRef.current = null;
      }
    };
  }, []);

  return {
    isWatching: unsubscribeRef.current !== null,
    currentDistance,
    error,
    startWatchingLocation,
    stopWatchingLocation,
  };
}

import { useCallback, useEffect, useRef, useState } from 'react';
import { PermissionStatus } from '@apps-in-toss/framework';

export interface PermissionGateConfig {
  getPermission: () => Promise<PermissionStatus>;
  openPermissionDialog: () => Promise<PermissionStatus>;
  onPermissionRequested?: (status: PermissionStatus) => void;
}

interface UsePermissionGateReturn {
  permission: PermissionStatus | null;
  isRequestingPermission: boolean;
  isExecuting: boolean;
  ensureAndRun: <T>(fn: () => Promise<T>) => Promise<T | undefined>;
  checkPermission: () => Promise<PermissionStatus>;
  requestPermission: () => Promise<PermissionStatus>;
}

const isAllowed = (status: PermissionStatus) => status === 'allowed';

export function usePermissionGate(
  config: PermissionGateConfig
): UsePermissionGateReturn {
  const [permission, setPermission] = useState<PermissionStatus | null>(null);
  const [isRequestingPermission, setIsRequestingPermission] = useState(false);
  const [isExecuting, setIsExecuting] = useState(false);

  const mountedRef = useRef<boolean>(false);
  const execCountRef = useRef<number>(0);
  const reqCountRef = useRef<number>(0);

  const runIfMounted = useCallback((fn: () => void) => {
    if (mountedRef.current) {
      fn();
    }
  }, []);

  const checkPermission = useCallback(async () => {
    const status = await config.getPermission();
    runIfMounted(() => setPermission(status));
    return status;
  }, [config, runIfMounted]);

  const requestPermission = useCallback(async () => {
    reqCountRef.current += 1;
    runIfMounted(() => setIsRequestingPermission(true));
    try {
      const status = await config.openPermissionDialog();
      runIfMounted(() => setPermission(status));
      config.onPermissionRequested?.(status);
      return status;
    } finally {
      reqCountRef.current -= 1;
      if (reqCountRef.current === 0) {
        runIfMounted(() => setIsRequestingPermission(false));
      }
    }
  }, [config, runIfMounted]);

  const ensureAndRun = useCallback(
    async <T>(fn: () => Promise<T>) => {
      const current = await checkPermission();
      const allowed =
        isAllowed(current) || isAllowed(await requestPermission());

      if (!allowed) {
        const error = new Error('권한을 거절했어요.');
        error.name = 'PermissionDeniedError';
        throw error;
      }

      execCountRef.current += 1;
      runIfMounted(() => setIsExecuting(true));
      try {
        const result = await fn();
        return result;
      } finally {
        execCountRef.current -= 1;

        if (execCountRef.current === 0) {
          runIfMounted(() => setIsExecuting(false));
        }
      }
    },
    [checkPermission, requestPermission, runIfMounted]
  );

  useEffect(() => {
    mountedRef.current = true;
    return () => {
      mountedRef.current = false;
    };
  }, []);

  return {
    permission,
    isRequestingPermission,
    isExecuting,
    ensureAndRun,
    checkPermission,
    requestPermission,
  };
}

import { useEffect, useState, useCallback } from "react";
import {
  Accuracy,
  getCurrentLocation,
  Location,
} from "@apps-in-toss/framework";
import { usePermissionGate } from "./usePermissionGate";
import { useToast } from "@toss/tds-react-native";

export function useCurrentLocation() {
  const [location, setLocation] = useState<Location | null>(null);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);
  const toast = useToast();
  const permissionGate = usePermissionGate({
    getPermission: () => getCurrentLocation.getPermission(),
    openPermissionDialog: () => getCurrentLocation.openPermissionDialog(),
    onPermissionRequested: (status) => toast.open(`권한 요청 결과: ${status}`),
  });

  const fetchLocation = useCallback(async () => {
    try {
      setLoading(true);
      setError(null);

      const response = await permissionGate.ensureAndRun(() =>
        getCurrentLocation({ accuracy: Accuracy.Balanced })
      );

      if (!response) {
        return;
      }

      setLocation(response);
    } catch (error) {
      let errorMessage = "위치 정보를 가져오는 데 실패했어요.";

      if (error instanceof Error) {
        errorMessage = error.message;
      }

      setError(errorMessage);
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    fetchLocation();
  }, []);

  return { location, loading, error, reloadLocation: fetchLocation };
}

