    } finally {
      reqCountRef.current -= 1;
      if (reqCountRef.current === 0) {
        runIfMounted(() => setIsRequestingPermission(false));
      }
    }
  }, [config, runIfMounted]);

  const ensureAndRun = useCallback(
    async <T>(fn: () => Promise<T>) => {
      const current = await checkPermission();
      const allowed =
        isAllowed(current) || isAllowed(await requestPermission());

      if (!allowed) {
        const error = new Error('권한을 거절했어요.');
        error.name = 'PermissionDeniedError';
        throw error;
      }

      execCountRef.current += 1;
      runIfMounted(() => setIsExecuting(true));
      try {
        const result = await fn();
        return result;
      } finally {
        execCountRef.current -= 1;

        if (execCountRef.current === 0) {
          runIfMounted(() => setIsExecuting(false));
        }
      }
    },
    [checkPermission, requestPermission, runIfMounted]
  );

  useEffect(() => {
    mountedRef.current = true;
    return () => {
      mountedRef.current = false;
    };
  }, []);

  return {
    permission,
    isRequestingPermission,
    isExecuting,
    ensureAndRun,
    checkPermission,
    requestPermission,
  };
}

import { useEffect, useState, useCallback } from 'react';
import { getNetworkStatus, NetworkStatus } from '@apps-in-toss/framework';

export function useNetworkStatus() {
  const [status, setStatus] = useState<NetworkStatus | null>(null);
  const [loading, setLoading] = useState(true);

  const fetchStatus = useCallback(async () => {
    setLoading(true);
    try {
      const networkStatus = await getNetworkStatus();
      setStatus(networkStatus);
    } catch (error) {
      console.error('❌ 네트워크 상태 가져오기 실패:', error);
      setStatus(null);
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    fetchStatus();
  }, [fetchStatus]);

  return {
    status,
    loading,
    refetch: fetchStatus,
  };
}

import { getLocale, getNetworkStatus, getSchemeUri, getPlatformOS } from "@apps-in-toss/framework";
