{
  "name": "threads",
  "version": "0.1.0",
  "description": "Save and manage Claude Code sessions as GitHub Gists",
  "author": {
    "name": "Marc Puig"
  },
  "license": "MIT",
  "repository": "github.com/mpuig/threads",
  "keywords": [
    "threads",
    "gist",
    "sessions",
    "persistence",
    "context",
    "ai-workflow",
    "agent-memory"
  ],
  "hooks": {
    "SessionStart": [
      {
        "matcher": "",
        "hooks": [
          {
            "type": "command",
            "command": "th prime"
          }
        ]
      }
    ]
  }
}
