      "version": "1.0.0",
      "author": {
        "name": "Nathan Onn"
      },
      "keywords": [
        "implementation-plan",
        "project-planning",
        "requirements",
        "wireframes",
        "phased-development",
        "architecture",
        "specifications"
      ],
