{
  "$schema": "https://anthropic.com/claude-code/marketplace.schema.json",
  "name": "nevaberry-plugins",
  "description": "Knowledge patches for programming languages and frameworks - updates Claude's knowledge about latest features",
  "owner": {
    "name": "Nevaberry",
    "email": "support@nevaberry.com"
  },
  "plugins": [
    {
      "name": "typescript-knowledge-patch",
      "source": "./plugins/typescript-knowledge-patch",
      "description": "TypeScript knowledge patch - updates Claude's knowledge about TypeScript 5.9+ features including import defer, --module node20, and TS7 transition",
      "category": "development"
    },
    {
      "name": "nextjs-knowledge-patch",
      "source": "./plugins/nextjs-knowledge-patch",
      "description": "Next.js knowledge patch - updates Claude's knowledge about Next.js 15.3-16.x features including proxy.ts, \"use cache\", Cache Components, navigation hooks, typed routes, and breaking changes",
      "category": "development"
    },
    {
      "name": "bun-knowledge-patch",
      "source": "./plugins/bun-knowledge-patch",
      "description": "Bun knowledge patch - updates Claude's knowledge about Bun 1.3.8 features including Bun-specific APIs, bun:* modules, Bun.serve(), bundler features, or any Bun features from 1.2+ (2025-2026)",
      "category": "development"
    },
    {
      "name": "rust-knowledge-patch",
      "source": "./plugins/rust-knowledge-patch",
      "description": "Rust knowledge patch - updates Claude's knowledge about Rust 1.85-1.93 features including Edition 2024, async closures, and new collection methods",
      "category": "development"
    },
    {
      "name": "dioxus-knowledge-patch",
      "source": "./plugins/dioxus-knowledge-patch",
      "description": "Dioxus knowledge patch - updates Claude's knowledge about Dioxus 0.7.3 features including subsecond hot-patching, WASM splitting, Manganis assets, and Stores",
      "category": "development"
    },
    {
      "name": "postgis-knowledge-patch",
      "source": "./plugins/postgis-knowledge-patch",
      "description": "PostGIS knowledge patch - updates Claude's knowledge about PostGIS 3.5-3.6.1 features including spatial functions, geography types, raster operations, and topology",
      "category": "development"
    },
    {
      "name": "postgresql-knowledge-patch",
      "source": "./plugins/postgresql-knowledge-patch",
      "description": "PostgreSQL knowledge patch - updates Claude's knowledge about PostgreSQL 17-18.1 features including MERGE, JSON functions, virtual generated columns, and temporal constraints",
      "category": "development"
    },
    {
      "name": "python-knowledge-patch",
      "source": "./plugins/python-knowledge-patch",
      "description": "Python knowledge patch - updates Claude's knowledge about Python 3.13-3.14 features including t-strings, deferred annotation evaluation, free-threaded Python, subinterpreters, and zstd compression",
      "category": "development"
    }
  ]
}
