{
  "$schema": "https://anthropic.com/claude-code/marketplace.schema.json",
  "name": "functional-claude",
  "owner": {
    "name": "nthplusio"
  },
  "plugins": [
    {
      "name": "wezterm-dev",
      "source": "./plugins/wezterm-dev",
      "description": "WezTerm terminal configuration and customization for Claude Code workflows",
      "version": "0.7.10"
    },
    {
      "name": "hyper-dev",
      "source": "./plugins/hyper-dev",
      "description": "Hyper terminal configuration and plugin development for Claude Code workflows",
      "version": "0.3.5"
    },
    {
      "name": "prisma-dev",
      "source": "./plugins/prisma-dev",
      "description": "Prisma ORM development workflows with schema analysis, migration safety, and query guidance",
      "version": "0.1.5"
    },
    {
      "name": "shadcn-dev",
      "source": "./plugins/shadcn-dev",
      "description": "shadcn/ui and Tailwind CSS v4 development workflows for React applications",
      "version": "0.1.6"
    },
    {
      "name": "pre-commit",
      "source": "./plugins/pre-commit",
      "description": "Pre-push checks for typechecking, linting, building, and testing with auto-detection and configurable enforcement",
      "version": "0.3.0"
    },
    {
      "name": "claude-plugin-dev",
      "source": "./plugins/claude-plugin-dev",
      "description": "Documentation and conventions for building Claude Code plugins with guided workflows, AI-assisted agent creation, and dynamic documentation caching",
      "version": "0.3.1"
    },
    {
      "name": "opentui-dev",
      "source": "./plugins/opentui-dev",
      "description": "OpenTUI terminal interface development with component design, layout architecture, and interactive prototyping",
      "version": "0.1.3"
    }
  ]
}

