{
  "$schema": "https://anthropic.com/claude-code/marketplace.schema.json",
  "name": "functional-claude",
  "owner": {
    "name": "nthplusio"
  },
  "plugins": [
    {
      "name": "wezterm-dev",
      "source": "./plugins/wezterm-dev",
      "description": "WezTerm terminal configuration and customization for Claude Code workflows. Includes tmux-style keybindings, visual effects (opacity, blur, themes), custom tab bars with Nerd Font icons, and Agent Deck for monitoring Claude Code agents",
      "version": "0.7.11"
    },
    {
      "name": "hyper-dev",
      "source": "./plugins/hyper-dev",
      "description": "Hyper terminal configuration and plugin development for Claude Code workflows. Covers keybindings, visual customization, theme creation, plugin discovery, and building Hyper plugins with the Electron/React API",
      "version": "0.3.6"
    },
    {
      "name": "prisma-dev",
      "source": "./plugins/prisma-dev",
      "description": "Prisma ORM development workflows with schema analysis, migration safety, and query guidance",
      "version": "0.1.6"
    },
    {
      "name": "shadcn-dev",
      "source": "./plugins/shadcn-dev",
      "description": "shadcn/ui and Tailwind CSS v4 development workflows for React applications. Covers component usage, form building with react-hook-form and zod, theming, data tables with TanStack Table, and Tailwind CSS v4 migration",
      "version": "0.1.9"
    },
    {
      "name": "code-quality",
      "source": "./plugins/code-quality",
      "description": "Deterministic code quality infrastructure — git hooks, lint-staged, and formatters that run on every commit and push, with guided setup and ecosystem detection",
      "version": "1.0.0"
    },
    {
      "name": "claude-plugin-dev",
      "source": "./plugins/claude-plugin-dev",
      "description": "Documentation and conventions for building Claude Code plugins with guided workflows, AI-assisted agent creation, and dynamic documentation caching",
      "version": "0.3.2"
    },
    {
      "name": "opentui-dev",
      "source": "./plugins/opentui-dev",
      "description": "OpenTUI terminal interface development with TypeScript/Bun. Supports Core imperative API, React reconciler, and Solid reconciler with components, flexbox layout, keyboard handling, and animations",
      "version": "0.1.4"
    },
    {
      "name": "dev-workflow",
      "source": "./plugins/dev-workflow",
      "description": "Development workflow validation and planning tools. Integrates a validation checklist into plan mode and provides a gut-check agent that reviews implementation plans before coding begins",
      "version": "0.2.1"
    },
    {
      "name": "tabby-dev",
      "source": "./plugins/tabby-dev",
      "description": "Tabby terminal configuration, SSH connections, and plugin development for Claude Code workflows. Covers visual customization, multi-chord hotkeys, SSH/serial/telnet connection profiles, and plugin discovery and development",
      "version": "0.1.1"
    },
    {
      "name": "agent-teams",
      "source": "./plugins/agent-teams",
      "description": "Agent team blueprints, coordination patterns, and reusable personas for parallel development. Pre-designed teams for research, feature development, code review, debugging, design, adaptive planning (7 modes), productivity, and brainstorming with multi-agent collaboration",
      "version": "0.10.0"
    },
    {
      "name": "gemini-cli",
      "source": "./plugins/gemini-cli",
      "description": "Gemini CLI integration for large context review and image generation via nano-banana extension. Validates CLI installation and environment on session start",
      "version": "0.4.1"
    }
  ]
}

