{
  "name": "trekker",
  "description": "AI-optimized issue tracker for coding workflows. Search-first workflow with persistent task memory across sessions.",
  "version": "1.7.4",
  "author": {
    "name": "Omercan Balandi",
    "url": "https://github.com/obsfx"
  },
  "repository": "https://github.com/obsfx/trekker-claude-code",
  "license": "MIT",
  "homepage": "https://github.com/obsfx/trekker-claude-code",
  "keywords": [
    "issue-tracker",
    "task-management",
    "ai-workflow",
    "agent-memory",
    "search-first",
    "context-recovery"
  ],
  "mcpServers": {
    "trekker": {
      "command": "node",
      "args": ["${CLAUDE_PLUGIN_ROOT}/mcp-server/dist/index.js"]
    }
  },
  "hooks": "./hooks/hooks.json"
}
