{
  "name": "hyperfleet-claude-plugins",
  "owner": {
    "name": "Alex Vulaj",
    "email": "avulaj@redhat.com"
  },
  "plugins": [
    {
      "name": "hyperfleet-architecture",
      "source": "./hyperfleet-architecture",
      "description": "Provides HyperFleet architecture knowledge for answering questions about design patterns, versioning strategies, and core principles"
    },
    {
      "name": "hyperfleet-jira",
      "source": "./hyperfleet-jira",
      "description": "JIRA integration for sprint management, task tracking, ticket hygiene audits, and story point estimation using jira-cli"
    },
    {
      "name": "hyperfleet-standards",
      "source": "./hyperfleet-standards",
      "description": "Audits HyperFleet repositories against team architecture standards dynamically fetched from the architecture repo, producing JIRA-ready gap specifications"
    },
    {
      "name": "hyperfleet-operational-readiness",
      "source": "./hyperfleet-operational-readiness",
      "description": "Audits HyperFleet repositories for operational readiness including health probes, dead man's switch metrics, retry logic, PDB, resource limits, and reliability documentation"
    },
    {
      "name": "hyperfleet-devtools",
      "source": "./hyperfleet-devtools",
      "description": "Developer productivity tools for HyperFleet - architecture documentation impact analysis and development assistance"
    }
  ]
}
