{
  "name": "perimetre-ai-toolkit",
  "owner": {
    "name": "Yanik Proulx"
  },
  "metadata": {
    "description": "Plugin marketplace for Claude and other AI tools",
    "version": "0.1.0",
    "pluginRoot": "./plugins"
  },
  "plugins": [
    {
      "name": "perimetre-brand-guidelines",
      "source": "./plugins/perimetre-brand-guidelines",
      "version": "1.0.0",
      "description": "Keep Claude aligned with Périmètre's visual identity. Loads brand guidelines — colours, typography, and logo usage — so every deliverable looks and feels on-brand.",
      "author": { "name": "Périmètre" },
      "tags": ["brand", "design", "perimetre"]
    },
    {
      "name": "perimetre-apps",
      "source": "./plugins/perimetre-apps",
      "version": "1.0.0",
      "description": "Périmètre patterns and best practices for JS/TS/React/Next.js apps. Includes framework patterns (error handling, services, tRPC, forms, GraphQL, caching, SEO), code review, React performance rules, composition patterns, and design engineering guidance.",
      "author": { "name": "Périmètre" },
      "tags": ["app", "react", "nextjs", "typescript", "code-review", "perimetre"]
    },
    {
      "name": "perimetre-design-system",
      "source": "./plugins/perimetre-design-system",
      "version": "1.0.0",
      "description": "Expert guidance for implementing and extending @perimetre/ui — a brand-aware React component library built on Tailwind CSS v4, CVA, and Radix UI. Covers design tokens, brand variants, component architecture, accessibility, and CVA patterns.",
      "author": { "name": "Périmètre" },
      "tags": ["design-system", "ui", "components", "tokens", "perimetre"]
    },
    {
      "name": "perimetre-wordpress",
      "source": "./plugins/perimetre-wordpress",
      "version": "1.0.0",
      "description": "WordPress development patterns and code review for Périmètre projects. Covers PHP standards, security, Gutenberg blocks, REST API, headless WP, and i18n.",
      "author": { "name": "Périmètre" },
      "tags": ["wordpress", "php", "gutenberg", "code-review", "perimetre"]
    },
    {
      "name": "perimetre-documents",
      "source": "./plugins/perimetre-documents",
      "version": "1.0.0",
      "description": "Professional document writing guidance for the Périmètre team — structure, clarity, tone, and formatting for reports, proposals, and presentations.",
      "author": { "name": "Périmètre" },
      "tags": ["document", "writing", "reports", "perimetre"]
    },
    {
      "name": "perimetre-discover",
      "source": "./plugins/perimetre-discover",
      "version": "1.0.0",
      "description": "Plugin discovery assistant for the Périmètre marketplace. Scans your project context or reads conversational cues, then recommends the right plugins with plain-language explanations and copy-pasteable install commands.",
      "author": { "name": "Périmètre" },
      "tags": ["discovery", "onboarding", "marketplace", "perimetre"]
    }
  ]
}

