{
  "name": "aiverse",
  "owner": {
    "name": "Piotr Zaniewski",
    "email": "piotr@cloudrumble.net"
  },
  "metadata": {
    "description": "A universe of AI-augmented development utilities, inspired by Unix philosophy",
    "version": "1.0.0",
    "homepage": "https://github.com/Piotr1215/aiverse",
    "keywords": ["ai", "development", "workflow", "terminal", "mcp", "utilities"]
  },
  "plugins": [
    {
      "name": "ai-coreutils",
      "source": {
        "source": "github",
        "repo": "Piotr1215/ai-coreutils",
        "branch": "main"
      },
      "description": "Essential AI-augmented dev utilities (commands, agents, hooks)",
      "version": "0.1.0",
      "author": {
        "name": "Piotr Zaniewski"
      },
      "category": "productivity",
      "keywords": ["development", "utilities", "coreutils", "documentation", "testing"],
      "strict": true
    },
    {
      "name": "vale-lsp",
      "source": "./plugins/vale-lsp",
      "description": "Vale prose linter LSP for markdown and MDX files",
      "version": "1.0.0",
      "author": {
        "name": "Piotr Zaniewski"
      },
      "category": "development",
      "strict": false,
      "lspServers": {
        "vale-ls": {
          "command": "vale-ls",
          "extensionToLanguage": {
            ".md": "markdown",
            ".mdx": "mdx"
          }
        }
      }
    }
  ]
}
