{
  "name": "asha-marketplace",
  "owner": {
    "name": "pknull",
    "email": "noreply@example.com"
  },
  "metadata": {
    "description": "Claude Code plugins for multi-perspective analysis, code review, output styling, and session coordination",
    "version": "1.11.0",
    "homepage": "https://github.com/pknull/asha-marketplace"
  },
  "plugins": [
    {
      "name": "panel-system",
      "description": "Dynamic multi-perspective analysis with persistence (v5.0.0). Supports --resume for interrupted panels, --list/--show for discovery, --format (github/json/markdown), --context. Per-phase state files in Work/panels/.",
      "source": "./plugins/panel",
      "strict": false
    },
    {
      "name": "code",
      "description": "Development workflows: orchestration patterns, code review, TDD, and specialized agents. Includes /code:review command, architect and codebase-historian agents, quality gates, Socratic planning, and swarm recipes.",
      "source": "./plugins/code",
      "strict": false
    },
    {
      "name": "write",
      "description": "Creative writing workflows: prose craft, worldbuilding, editing, AI detection, and storytelling agents. Includes 10 agents (fiction-writer, prose-analysis, ai-detector, intimacy-designer, manuscript-editor, outline-architect, prose-writer, consistency-checker, developmental-editor, line-editor) with chapter/manuscript/character recipes.",
      "source": "./plugins/write",
      "strict": false
    },
    {
      "name": "output-styles",
      "description": "Switchable output styles for Claude Code responses. Includes 8 styles (ultra-concise, bullet-points, genui, html-structured, markdown-focused, table-based, tts-summary, yaml-structured) with /style command.",
      "source": "./plugins/output-styles",
      "strict": false
    },
    {
      "name": "asha",
      "description": "Cognitive scaffold framework with cross-project identity layer (~/.asha/). Session coordination, Memory Bank, keeper profile calibration, vector DB semantic search, ReAct analysis, and credential protection (block-secrets hook). Identity persists across all Asha-enabled projects.",
      "source": "./plugins/asha",
      "strict": false
    },
    {
      "name": "image",
      "description": "Image generation workflows: Stable Diffusion prompt engineering, ComfyUI workflow design, parameter optimization, and LoRA/model selection for AI art generation.",
      "source": "./plugins/image",
      "strict": false
    },
    {
      "name": "scheduler",
      "description": "Scheduled task execution for Claude Code. Manage cron-style recurring tasks with natural language time expressions. Supports cron and systemd backends with rate limiting, audit logging, and security constraints.",
      "source": "./plugins/schedule",
      "strict": false
    }
  ]
}
