{
  "name": "custom-plugin-api-design",
  "version": "2.0.0",
  "description": "Enterprise-grade plugin API design system - Complete framework for building, securing, and scaling custom plugin systems based on 65+ developer roadmap roles",
  "author": "Plugin Architecture Labs",
  "repository": "https://github.com/pluginagentmarketplace/custom-plugin-api-design",
  "license": "SEE LICENSE IN LICENSE",
  "keywords": [
    "plugin-api-design",
    "api-architecture",
    "plugin-system",
    "backend",
    "devops",
    "security",
    "scalability",
    "system-design",
    "microservices",
    "enterprise-patterns"
  ],
  "agents": [
    {
      "name": "API Design Architect",
      "path": "./agents/01-api-architect.md",
      "description": "Expert in API architecture, contracts, and design - aligned with API Design, System Design, Software Architect roles"
    },
    {
      "name": "Backend Development Patterns",
      "path": "./agents/02-backend-patterns.md",
      "description": "Backend development expertise - Node.js, Python, Go, Java, frameworks, and production patterns"
    },
    {
      "name": "Database & Performance",
      "path": "./agents/03-database-performance.md",
      "description": "Database design, optimization, and performance tuning - SQL, MongoDB, Redis, caching strategies"
    },
    {
      "name": "DevOps & Infrastructure",
      "path": "./agents/04-devops-infrastructure.md",
      "description": "Infrastructure, deployment, and operations - Docker, Kubernetes, AWS, Terraform, CI/CD"
    },
    {
      "name": "Security & Compliance",
      "path": "./agents/05-security-compliance.md",
      "description": "Security architecture, authentication, authorization - Cyber security best practices and compliance"
    },
    {
      "name": "Frontend & Integration",
      "path": "./agents/06-frontend-integration.md",
      "description": "Frontend development and API consumption - React, TypeScript, client patterns"
    },
    {
      "name": "Advanced Scaling Patterns",
      "path": "./agents/07-scaling-patterns.md",
      "description": "Enterprise patterns, microservices, async operations, AI agents, advanced system design"
    }
  ],
  "commands": [
    {
      "name": "architect",
      "path": "./commands/architect.md",
      "description": "Design your API system architecture"
    },
    {
      "name": "audit",
      "path": "./commands/audit.md",
      "description": "Audit your API design against best practices"
    },
    {
      "name": "roadmap",
      "path": "./commands/roadmap.md",
      "description": "Get implementation roadmap for your API"
    },
    {
      "name": "secure",
      "path": "./commands/secure.md",
      "description": "Secure your API - authentication, authorization, compliance"
    }
  ],
  "skills": [
    {
      "name": "api-architecture",
      "path": "./skills/api-architecture/SKILL.md",
      "description": "REST, GraphQL, and hybrid API architecture patterns"
    },
    {
      "name": "backend-patterns",
      "path": "./skills/backend-patterns/SKILL.md",
      "description": "Production-grade backend patterns for Node.js, Python, Go, Java"
    },
    {
      "name": "database-patterns",
      "path": "./skills/database-patterns/SKILL.md",
      "description": "Database design, optimization, caching with SQL, MongoDB, Redis"
    },
    {
      "name": "devops-patterns",
      "path": "./skills/devops-patterns/SKILL.md",
      "description": "Infrastructure and deployment with Docker, Kubernetes, CI/CD"
    },
    {
      "name": "security-patterns",
      "path": "./skills/security-patterns/SKILL.md",
      "description": "Security architecture, auth, encryption, and compliance"
    },
    {
      "name": "frontend-patterns",
      "path": "./skills/frontend-patterns/SKILL.md",
      "description": "Frontend development and API integration patterns"
    },
    {
      "name": "scaling-patterns",
      "path": "./skills/scaling-patterns/SKILL.md",
      "description": "Microservices, event-driven, and distributed system patterns"
    }
  ]
}

