### Cache Invalidation Strategies

```dart
// TTL-based cache with Riverpod
class CacheEntry<T> {
  final T value;
  final DateTime createdAt;
  final Duration ttl;

  CacheEntry({
    required this.value,
    required this.ttl,
  }) : createdAt = DateTime.now();

  bool get isExpired => DateTime.now().difference(createdAt) > ttl;
}

final cachedUserProvider = FutureProvider.autoDispose<User>((ref) async {
  final cache = ref.watch(cacheManagerProvider);
  final repository = ref.watch(userRepositoryProvider);

  final cached = cache.get<User>('user_data');

  if (cached != null && !cached.isExpired) {
    return cached.value;
  }

  final user = await repository.getUser();
  cache.set('user_data', CacheEntry(
    value: user,
    ttl: const Duration(hours: 1),
  ));

  return user;
});

// Manual invalidation
class UserScreen extends ConsumerWidget {
  @override
  Widget build(BuildContext context, WidgetRef ref) {
    final user = ref.watch(cachedUserProvider);

    return user.when(
      data: (user) => Column(
        children: [
          Text(user.name),
          ElevatedButton(
            onPressed: () {
              // Force refresh
              ref.refresh(cachedUserProvider);
            },
            child: const Text('Refresh'),
          ),
        ],
      ),
      loading: () => const CircularProgressIndicator(),
      error: (error, _) => Text('Error: $error'),
    );
  }
}
