import SwiftUI

struct ContentView: View {
  @State private var count = 0
  @State private var isLoading = false
  @State private var error: Error?

  var body: some View {
    VStack {
      if isLoading {
        ProgressView()
      } else if let error = error {
        ErrorView(error: error, retry: loadData)
      } else {
        Text("Count: \(count)")
          .font(.headline)
        Button(action: { count += 1 }) {
          Text("Increment")
