### Contract Interaction Hook
```typescript
// hooks/useNFTMint.ts
import { useWriteContract, useWaitForTransactionReceipt } from 'wagmi';
import { parseEther } from 'viem';
import { NFT_ABI } from '@/constants/abis';
import { NFT_ADDRESS } from '@/constants/addresses';

export function useNFTMint() {
  const {
    writeContract,
    data: hash,
    isPending,
    error: writeError
  } = useWriteContract();

  const {
    isLoading: isConfirming,
    isSuccess,
    error: confirmError
  } = useWaitForTransactionReceipt({ hash });

  const mint = async (quantity: number) => {
    const price = parseEther('0.08');

    writeContract({
      address: NFT_ADDRESS,
      abi: NFT_ABI,
      functionName: 'mint',
      args: [BigInt(quantity)],
      value: price * BigInt(quantity),
    });
  };

  return {
    mint,
    isPending,
    isConfirming,
    isSuccess,
    hash,
    error: writeError || confirmError,
  };
}
```

### EIP-712 Typed Data Signing
```typescript
import { useSignTypedData, useAccount } from 'wagmi';

const PERMIT_TYPES = {
  Permit: [
    { name: 'owner', type: 'address' },
    { name: 'spender', type: 'address' },
    { name: 'value', type: 'uint256' },
    { name: 'nonce', type: 'uint256' },
    { name: 'deadline', type: 'uint256' },
  ],
} as const;

export function useSignPermit() {
  const { address } = useAccount();
  const { signTypedDataAsync } = useSignTypedData();

  const signPermit = async (params: PermitParams) => {
    if (!address) throw new Error('Wallet not connected');

    const signature = await signTypedDataAsync({
      domain: {
        name: params.tokenName,
        version: '1',
        chainId: params.chainId,
        verifyingContract: params.tokenAddress,
      },
      types: PERMIT_TYPES,
      primaryType: 'Permit',
      message: {
        owner: address,
        spender: params.spender,
        value: params.value,
        nonce: params.nonce,
        deadline: params.deadline,
      },
    });

    return signature;
  };

  return { signPermit };
}
```

### Error Handling Utilities
```typescript
// utils/errors.ts
export function parseWeb3Error(error: unknown): string {
  if (!error) return 'Unknown error';

  const errorString = error instanceof Error ? error.message : String(error);

  if (errorString.includes('user rejected') ||
      errorString.includes('User denied')) {
    return 'Transaction was rejected';
  }

  if (errorString.includes('insufficient funds')) {
    return 'Insufficient funds for transaction';
  }

  if (errorString.includes('execution reverted')) {
    const match = errorString.match(/reason="([^"]+)"/);
    if (match) return `Transaction would fail: ${match[1]}`;
    return 'Transaction would fail. Check contract conditions';
  }

  return 'Transaction failed. Please try again';
}
```

