{
  "name": "claude-flow",
  "description": "Git workflow commands and the Will Haacket agent - a modern take on GitHub Flow aliases",
  "version": "0.1.0",
  "author": {
    "name": "poindexter12"
  },
  "repository": "https://github.com/poindexter12/claude-flow",
  "license": "MIT",
  "keywords": ["git", "workflow", "github-flow", "conventional-commits"],
  "agents": [
    "./agents/will-haacket.md"
  ],
  "commands": [
    "./commands/clean.md",
    "./commands/done.md",
    "./commands/save.md",
    "./commands/start.md",
    "./commands/sync.md",
    "./commands/undo.md",
    "./commands/wip.md"
  ],
  "skills": [
    "./skills/branch-naming",
    "./skills/conventional-commit"
  ],
  "hooks": {
    "PreToolUse": [
      {
        "matcher": "Bash",
        "hooks": [
          {
            "type": "command",
            "command": "python3 ${CLAUDE_PLUGIN_ROOT}/scripts/validate_commit.py"
          },
          {
            "type": "command",
            "command": "python3 ${CLAUDE_PLUGIN_ROOT}/scripts/validate_branch.py"
          }
        ]
      }
    ]
  }
}
