      "source": "./plugins/mcp-servers"
    },
    {
      "name": "permission-sync",
      "description": "Synchronize Claude Code permissions across all your projects",
      "source": "./plugins/permission-sync"
    },
    {
      "name": "knowledge-sync",
      "description": "Sync agents, skills, commands, and docs from external knowledge repository",
      "source": "./plugins/knowledge-sync"
