{
  "name": "rivet",
  "version": "0.0.2",
  "description": "Lock linguistic decisions in your codebase. Living architecture, anchored requirements, locked terminology.",
  "author": {
    "name": "ratacat"
  },
  "repository": "https://github.com/ratacat/rivet",
  "license": "MIT",
  "keywords": ["architecture", "terminology", "requirements", "ai-context"],
  "skills": "./skills/",
  "hooks": {
    "SessionStart": [
      {
        "matcher": "",
        "hooks": [
          {
            "type": "command",
            "command": "bash ${CLAUDE_PLUGIN_ROOT}/scripts/check-rivet.sh"
          }
        ]
      }
    ]
  }
}
