files..claude-plugin/plugin.json:
{
  "name": "sugar",
  "version": "3.4.0",
  "description": "üç∞ Sugar - The autonomous layer for AI coding agents",
  "author": {
    "name": "Steven Leggett",
    "email": "contact@roboticforce.io"
  },
  "homepage": "https://github.com/roboticforce/sugar",
  "repository": "https://github.com/roboticforce/sugar",
  "license": "MIT",
  "keywords": [
    "autonomous",
    "development",
    "ai",
    "task-management",
    "automation",
    "agents",
    "enterprise"
  ],
  "requires": {
    "claude-code": ">=1.0.0",
    "python": ">=3.11"
  },
  "capabilities": {
    "commands": true,
    "agents": true,
    "hooks": true,
    "mcp": true
  },
  "installation": {
    "prerequisites": [
      "Python 3.11 or higher must be installed",
      "Sugar CLI must be installed: pip install sugarai",
      "Initialize Sugar in your project: sugar init"
    ],
    "verification": [
      "sugar --version",
      "sugar status"
    ]
  },
  "configuration": {
    "auto_detect_sugar": true,
    "sugar_command": "sugar",
    "enable_autonomous_mode": false,
    "auto_task_creation": true
  }
}
