{
  "name": "my-claude-skills",
  "owner": {
    "name": "sandbornm"
  },
  "metadata": {
    "description": "Security analysis skills for Claude Code: angr (static binary), Frida (dynamic binary), and pcap (network capture)",
    "version": "1.1.0"
  },
  "plugins": [
    {
      "name": "angr-analyzer",
      "source": "./angr-analyzer",
      "description": "Static binary analysis with angr: decompilation, CFG recovery, symbolic execution, vulnerability detection"
    },
    {
      "name": "frida-instrument",
      "source": "./frida-instrument",
      "description": "Dynamic binary instrumentation with Frida: function tracing, API hooking, memory inspection, Stalker tracing"
    },
    {
      "name": "pcap-analyzer",
      "source": "./pcap-analyzer",
      "description": "Network capture analysis with scapy: stream extraction, DNS/HTTP parsing, credential detection, anomaly detection, file carving"
    }
  ]
}
