{
  "$schema": "https://anthropic.com/claude-code/marketplace.schema.json",
  "name": "claude-plugins",
  "version": "1.0.0",
  "description": "Directory of Claude Code extensions including development tools, productivity plugins, and MCP integrations",
  "owner": {
    "name": "saru",
    "email": "contact@saru.id"
  },
  "plugins": [
    {
      "name": "dart-lsp",
      "description": "Dart/Flutter language server for advanced code analysis",
      "version": "1.0.0",
      "author": {
        "name": "saru",
        "email": "contact@saru.id"
      },
      "source": "./plugins/dart-lsp",
      "category": "development",
      "strict": false,
      "lspServers": {
        "dart": {
          "command": "dart",
          "args": [
            "language-server",
            "--protocol=lsp",
            "--client-id=saru-claude-plugins.dart-lsp",
            "--client-version=1.0.0"
          ],
          "extensionToLanguage": {
            ".dart": "dart"
          },
          "initializationOptions": {
            "suggestFromUnimportedLibraries": true,
            "onlyAnalyzeProjectsWithOpenFiles": false,
            "closingLabels": false,
            "outline": false,
            "flutterOutline": false,
            "allowOpenUri": false
          },
          "loggingConfig": {
            "args": [
              "--instrumentation-log-file",
              "${CLAUDE_PLUGIN_LSP_LOG_FILE}"
            ]
          }
        }
      }
    }
  ]
}
