{
  "$schema": "https://anthropic.com/claude-code/marketplace.schema.json",
  "name": "bershadsky-claude-tools",
  "version": "1.0.0",
  "description": "Personal Claude Code skills and hooks for workflow automation",
  "owner": {
    "name": "Sergey Bershadsky",
    "email": "sergio@bershadsky.dev"
  },
  "repository": "https://github.com/sergio-bershadsky/ai",
  "plugins": [
    {
      "name": "git",
      "source": "./plugins/git",
      "description": "Git workflow automation: conventional commits, auto-staging, uncommitted changes protection",
      "version": "1.0.0",
      "category": "productivity"
    },
    {
      "name": "settings-sync",
      "source": "./plugins/settings-sync",
      "description": "Sync Claude settings across ephemeral VMs via Git backup",
      "version": "1.0.0",
      "category": "productivity"
    },
    {
      "name": "frappe-dev",
      "source": "./plugins/frappe-dev",
      "description": "Professional Frappe Framework v15 development toolkit with multi-layer architecture patterns, DocType scaffolding, REST API v2, and testing",
      "version": "1.0.0",
      "category": "development"
    },
    {
      "name": "marketplace-validator",
      "source": "./plugins/marketplace-validator",
      "description": "Validates marketplace.json schema after every edit in this repository",
      "version": "1.0.0",
      "category": "development"
    },
    {
      "name": "django-dev",
      "source": "./plugins/django-dev",
      "description": "Opinionated Django development toolkit with Ninja API, Unfold admin, pytest, Dynaconf, uv, and Docker patterns",
      "version": "1.0.0",
      "category": "development"
    },
    {
      "name": "secondbrain",
      "source": "./plugins/secondbrain",
      "description": "Knowledge base scaffolding with microdatabases, VitePress portal, configurable entities (ADRs, Notes, Tasks, Discussions), freshness tracking, and background refinement",
      "version": "1.0.0",
      "category": "productivity"
    },
    {
      "name": "replit-prompts",
      "source": "./plugins/replit-prompts",
      "description": "Generate optimized prompts, PRDs, and task plans for Replit Agent that maximize AI understanding and minimize iterations",
      "version": "1.0.0",
      "category": "productivity"
    }
  ]
}
