{
  "name": "sevii-agent-marketplace",
  "owner": {
    "name": "sevii",
    "email": ""
  },
  "metadata": {
    "description": "A curated collection of Claude Code plugins for enhanced development workflows",
    "version": "1.0.0",
    "pluginRoot": "./plugins"
  },
  "plugins": [
    {
      "name": "elevator-music",
      "source": "./plugins/elevator-music",
      "description": "Plays soothing elevator music while Claude Code is waiting for user input",
      "version": "1.0.0",
      "category": "productivity",
      "keywords": ["hooks", "music", "waiting", "ux"],
      "strict": true
    },
    {
      "name": "hook-logger",
      "source": "./plugins/hook-logger",
      "description": "Logs all Claude Code hook invocations to a JSON file with automatic rotation",
      "version": "1.0.0",
      "category": "development",
      "keywords": ["hooks", "logging", "debugging", "development", "monitoring"],
      "strict": true
    },
    {
      "name": "elevator-notifications",
      "source": "./plugins/elevator-notifications",
      "description": "Sends OS notifications when Claude Code is waiting for user input",
      "version": "1.0.0",
      "category": "productivity",
      "keywords": ["hooks", "notifications", "waiting", "ux", "productivity"],
      "strict": true
    },
    {
      "name": "thymeleaf-skill",
      "source": "./plugins/thymeleaf-skill",
      "description": "Comprehensive Thymeleaf 3.1 reference skill providing template syntax, expression language, iteration, conditionals, layout composition, and inlining guidance",
      "version": "1.0.0",
      "category": "development",
      "keywords": ["skill", "thymeleaf", "java", "templates", "spring", "html", "server-side-rendering"],
      "strict": true
    }
  ]
}
