{
  "name": "beads",
  "description": "AI-supervised issue tracker for coding workflows. Manage tasks, discover work, and maintain context with simple CLI commands.",
  "version": "0.30.3",
  "author": {
    "name": "Shane Holloman",
    "url": "https://github.com/shaneholloman"
  },
  "repository": "https://github.com/shaneholloman/beads",
  "license": "MIT",
  "homepage": "https://github.com/shaneholloman/beads",
  "keywords": [
    "issue-tracker",
    "task-management",
    "ai-workflow",
    "agent-memory",
    "mcp-server"
  ],
  "mcpServers": {
    "beads": {
      "command": "uv",
      "args": [
        "--directory",
        "${CLAUDE_PLUGIN_ROOT}/adapters/mcp",
        "run",
        "mcp-beads"
      ],
      "env": {}
    }
  }
}
