{
  "$schema": "https://anthropic.com/claude-code/marketplace.schema.json",
  "name": "harness",
  "version": "1.0.0",
  "description": "Custom plugins for Claude Code",
  "owner": {
    "name": "PND"
  },
  "plugins": [
    {
      "name": "todo-enforcer",
      "source": "./todo-enforcer",
      "description": "Enforces all TODO items are completed before Claude can stop working"
    },
    {
      "name": "comment-checker",
      "source": "./comment-checker",
      "description": "Detects and warns about unnecessary comments in code changes, encouraging self-documenting code"
    }
  ]
}
