{
  "$schema": "https://anthropic.com/claude-code/marketplace.schema.json",
  "name": "drupal-best-practices",
  "owner": {
    "name": "Theodoros Ploumis"
  },
  "metadata": {
    "description": "Drupal development best practices skills for Claude Code including blocks, config parser, fields, forms, nodes, content entities, taxonomy, text formats, theming, and views",
    "version": "1.0.0"
  },
  "plugins": [
    {
      "name": "drupal-blocks",
      "source": "./ai/skills/blocks",
      "description": "Create reusable custom blocks and plugins with disciplined naming and configuration. Provides guidance on block type creation, field planning, view mode reuse, and caching expectations for Drupal blocks."
    },
    {
      "name": "drupal-config-parser",
      "source": "./ai/skills/config-parser",
      "description": "Parse and validate Drupal configuration YAML files for best practices compliance. Analyzes exported configuration files to check naming conventions, structure compliance, and configuration patterns without requiring a running Drupal instance."
    },
    {
      "name": "drupal-fields",
      "source": "./ai/skills/fields",
      "description": "Field management and configuration best practices for Drupal. Provides guidance on field creation, configuration, storage settings, and field usage patterns in Drupal content types and entities."
    },
    {
      "name": "drupal-forms",
      "source": "./ai/skills/forms",
      "description": "Form API implementation and best practices for Drupal. Covers form creation, validation, submission handling, security considerations, and form theming in Drupal's Form API."
    },
    {
      "name": "drupal-nodes",
      "source": "./ai/skills/nodes",
      "description": "Node content type management and configuration best practices. Provides guidance on creating content types, managing node fields, view modes, and node-related functionality in Drupal."
    },
    {
      "name": "drupal-content-entities",
      "source": "./ai/skills/other-content-entities",
      "description": "Custom content entity development and configuration for Drupal. Covers creating custom entity types, defining entity fields, entity storage, and entity display configuration."
    },
    {
      "name": "drupal-taxonomy",
      "source": "./ai/skills/taxonomy",
      "description": "Taxonomy vocabulary and term management best practices for Drupal. Provides guidance on creating vocabularies, managing taxonomy terms, and implementing taxonomy-based content classification."
    },
    {
      "name": "drupal-text-formats-editors",
      "source": "./ai/skills/text-formats-editors",
      "description": "Text formats and editors configuration and security for Drupal. Covers creating text formats, configuring editors, managing filter settings, and ensuring text format security."
    },
    {
      "name": "drupal-theming",
      "source": "./ai/skills/theming",
      "description": "Theme development and frontend best practices for Drupal. Provides guidance on creating themes, using Twig templates, theme preprocessing, asset management, and responsive design in Drupal."
    },
    {
      "name": "drupal-views",
      "source": "./ai/skills/views",
      "description": "Views configuration and optimization for Drupal. Covers creating views, managing displays, optimizing queries, and implementing complex data presentations using Drupal Views module."
    }
  ]
}
