{
  "$schema": "https://code.claude.com/schemas/marketplace.json",
  "name": "thrashr888-agent-kit",
  "description": "Development workflows, release automation, and best practices for Rust, Python, Next.js, and Swift projects",
  "version": "0.3.0",
  "owner": {
    "name": "Paul Thrasher",
    "email": "paul@thrasher.dev"
  },
  "repository": "https://github.com/thrashr888/thrashr888-agent-kit",
  "plugins": [
    {
      "name": "style-docs",
      "source": "./skills/style-docs",
      "description": "5-style documentation system with templates for plans, specs, skills, RFCs, ADRs, bug reports, feature requests, and PRs",
      "version": "0.1.0",
      "author": {
        "name": "Paul Thrasher"
      },
      "tags": ["documentation", "templates", "workflow"]
    },
    {
      "name": "github-releases",
      "source": "./skills/github-releases",
      "description": "Release workflows for Rust CLI, macOS apps, Python/uv, and Next.js projects",
      "version": "0.2.0",
      "author": {
        "name": "Paul Thrasher"
      },
      "tags": ["releases", "versioning", "changelog", "ci-cd"]
    },
    {
      "name": "rust-development",
      "source": "./skills/rust-development",
      "description": "Rust development workflow with quality gates, testing, and iteration patterns",
      "version": "0.1.0",
      "author": {
        "name": "Paul Thrasher"
      },
      "tags": ["rust", "development", "testing", "workflow"]
    },
    {
      "name": "rust-onboard",
      "source": "./skills/rust-onboard",
      "description": "Onboard new Rust projects with CI/CD, tooling, and Homebrew distribution",
      "version": "0.1.0",
      "author": {
        "name": "Paul Thrasher"
      },
      "tags": ["rust", "onboarding", "ci-cd", "homebrew"]
    },
    {
      "name": "rust-release",
      "source": "./skills/rust-release",
      "description": "Release Rust CLI tools with multi-platform binaries and Homebrew tap updates",
      "version": "0.1.0",
      "author": {
        "name": "Paul Thrasher"
      },
      "tags": ["rust", "releases", "homebrew", "github-actions"]
    },
    {
      "name": "rust-best-practices",
      "source": "./skills/rust-best-practices",
      "description": "Rust coding best practices for idiomatic, efficient, and maintainable code",
      "version": "0.1.0",
      "author": {
        "name": "Paul Thrasher"
      },
      "tags": ["rust", "best-practices", "patterns", "idioms"]
    },
    {
      "name": "python-uv",
      "source": "./skills/python-uv",
      "description": "Modern Python development with uv package manager, pytest, FastAPI, and Django",
      "version": "0.1.0",
      "author": {
        "name": "Paul Thrasher"
      },
      "tags": ["python", "uv", "fastapi", "django", "pytest"]
    },
    {
      "name": "hcp-terraform",
      "source": "./skills/hcp-terraform",
      "description": "HCP Terraform (Terraform Cloud) workflow for remote plan and apply",
      "version": "0.1.0",
      "author": {
        "name": "Paul Thrasher"
      },
      "tags": ["terraform", "hcp", "infrastructure", "aws"]
    },
    {
      "name": "homebrew-tap",
      "source": "./skills/homebrew-tap",
      "description": "Manage Homebrew taps for distributing CLI tools and macOS apps",
      "version": "0.1.0",
      "author": {
        "name": "Paul Thrasher"
      },
      "tags": ["homebrew", "distribution", "macos", "cli"]
    },
    {
      "name": "makefile-patterns",
      "source": "./skills/makefile-patterns",
      "description": "Makefile patterns for development, building, testing, and releasing",
      "version": "0.1.0",
      "author": {
        "name": "Paul Thrasher"
      },
      "tags": ["makefile", "build", "automation", "workflow"]
    }
  ]
}
