files..claude-plugin/plugin.json:
{
  "name": "iterm2-controller",
  "version": "1.0.0",
  "description": "Gives Claude Code control over iTerm2 terminals via AppleScript",
  "author": { "name": "Niall Conroy" },
  "repository": "https://github.com/TinyCamera/iterm2-controller",
  "license": "MIT",
  "keywords": ["iterm2", "terminal", "applescript", "mcp"],
  "mcpServers": {
    "iterm2-mcp": {
      "command": "${CLAUDE_PLUGIN_ROOT}/.venv/bin/python3",
      "args": ["${CLAUDE_PLUGIN_ROOT}/server.py"]
    }
  }
}
