{
  "name": "brand-voice",
  "version": "1.0.0",
  "description": "Transforms scattered brand documents and conversation transcripts into enforceable AI guardrails. Searches across Confluence, Google Drive, Box, SharePoint, Slack, and conversation intelligence platforms like Gong and Granola to discover how your company actually communicates. Creates LLM-ready brand guidelines and continuously validates AI-generated content — from sales emails to marketing copy — ensuring every output matches your established voice, tone, and positioning. Surfaces open branding questions for team discussion when ambiguity is found.",
  "author": {
    "name": "Tribe AI"
  },
  "keywords": ["brand-voice", "sales", "marketing", "content-creation", "guidelines", "discovery", "enterprise"]
}
