{
  "name": "claude-skills",
  "description": "A collection of Claude Code skills providing specialized capabilities through modular plugins.",
  "owner": {
    "name": "tsilva"
  },
  "plugins": [
    {
      "name": "project-readme-author",
      "source": "./plugins/project-readme-author",
      "description": "Create, modify, validate, and optimize README.md files following GitHub best practices.",
      "version": "2.5.1"
    },
    {
      "name": "project-logo-author",
      "source": "./plugins/project-logo-author",
      "description": "Generates professional logos with native transparent backgrounds. Requires mcp-openrouter MCP server. Use when asked to 'generate a logo' or 'create logo'.",
      "version": "5.1.0"
    },
{
      "name": "bash-output-styler",
      "source": "./plugins/bash-output-styler",
      "description": "Applies gorgeous terminal styling to bash scripts using gum with ANSI fallback.",
      "version": "2.0.0"
    },
    {
      "name": "python-output-styler",
      "source": "./plugins/python-output-styler",
      "description": "Applies gorgeous terminal styling to Python scripts using Rich with plain-text fallback.",
      "version": "1.0.0"
    },
    {
      "name": "project-spec-extractor",
      "source": "./plugins/project-spec-extractor",
      "description": "Analyzes a codebase and generates a pure requirements specification (SPEC.md) with no implementation details.",
      "version": "1.0.0"
    }
  ]
}
