      "category": "cms",
      "tags": ["umbraco", "dotnet", "aspnet-core", "lit", "typescript"]
    },
    {
      "name": "optimizely",
      "source": "./plugins/optimizely-cms-analyzer",
      "description": "Optimizely CMS/Content Cloud analyzer for content modeling, .NET patterns, and headless APIs",
      "category": "cms",
      "tags": ["optimizely", "episerver", "dotnet", "cms", "content-cloud"]
    },
    {
      "name": "optimizely-exp",
      "source": "./plugins/optimizely-experimentation-analyzer",
