{
  "$schema": "https://raw.githubusercontent.com/UNCTAD-eRegistrations/plugin-marketplace/main/.claude-plugin/marketplace.schema.json",
  "name": "unctad-digital-government",
  "description": "Official plugin marketplace for UNCTAD Digital Government Tools — eRegistrations platform, BPA service development, government procedure reform, and digital public services",
  "owner": {
    "name": "UNCTAD Trade Facilitation Section",
    "email": "tf-tools@unctad.org"
  },
  "plugins": [
    {
      "name": "bpa-instances",
      "description": "Configure Claude Code MCP connections for all eRegistrations BPA instances (Nigeria, Jamaica, Lesotho, El Salvador, Kenya, Cuba, Colombia, Gambia, Bhutan). Install this first — all other plugins depend on it. Requires the BPA MCP server binary: run `uv tool install mcp-eregistrations-bpa` before using.",
      "author": {
        "name": "UNCTAD Trade Facilitation Section",
        "email": "tf-tools@unctad.org"
      },
      "source": "./plugins/bpa-instances",
      "category": "integration"
    },
    {
      "name": "service-documentation",
      "description": "Generate citizen-facing HTML service manuals (single and batch) and export structured Excel documentation covering fields, determinants, roles, bots, costs, and document requirements.",
      "author": {
        "name": "UNCTAD Trade Facilitation Section",
        "email": "tf-tools@unctad.org"
      },
      "source": "./plugins/service-documentation",
      "category": "documentation"
    },
    {
      "name": "service-testing",
      "description": "Validate BPA service configuration across 4 test suites, score against UNCTAD quality standards (0–100), and snapshot services for regression testing.",
      "author": {
        "name": "UNCTAD Trade Facilitation Section",
        "email": "tf-tools@unctad.org"
      },
      "source": "./plugins/service-testing",
      "category": "design"
    },
    {
      "name": "service-builder",
      "description": "Design and build new eRegistrations services from scratch. Guides through form design, determinant configuration, role assignment, and registration linking. Includes service-architect agent.",
      "author": {
        "name": "UNCTAD Trade Facilitation Section",
        "email": "tf-tools@unctad.org"
      },
      "source": "./plugins/service-builder",
      "category": "design"
    },
    {
      "name": "service-migration",
      "description": "Copy BPA services between instances (e.g., staging to production, or Jamaica to Lesotho) with dry-run validation and post-migration verification. Includes diff tool for cross-instance auditing.",
      "author": {
        "name": "UNCTAD Trade Facilitation Section",
        "email": "tf-tools@unctad.org"
      },
      "source": "./plugins/service-migration",
      "category": "design"
    },
    {
      "name": "bpa-debug",
      "description": "Scan, investigate, and fix BPA service configuration issues. Autonomous debug agent provides dependency-ordered fix plans with verification and rollback.",
      "author": {
        "name": "UNCTAD Trade Facilitation Section",
        "email": "tf-tools@unctad.org"
      },
      "source": "./plugins/bpa-debug",
      "category": "debugging"
    },
    {
      "name": "role-configurator",
      "description": "Design and configure multi-agency role structures and processing workflows. Supports single-agency, two-stage approval, and complex multi-track patterns. Includes workflow-designer agent.",
      "author": {
        "name": "UNCTAD Trade Facilitation Section",
        "email": "tf-tools@unctad.org"
      },
      "source": "./plugins/role-configurator",
      "category": "design"
    },
    {
      "name": "institution-setup",
      "description": "Configure institutions and assign them to registrations and roles. Interactive wizard for fresh country deployments. Includes deployment-configurator agent for full-instance setup.",
      "author": {
        "name": "UNCTAD Trade Facilitation Section",
        "email": "tf-tools@unctad.org"
      },
      "source": "./plugins/institution-setup",
      "category": "administration"
    },
    {
      "name": "bot-mappings",
      "description": "Create and manage BPA bot integrations and field mappings. AI-powered mapping suggestions, health summaries, validation, and atomic bulk replacement.",
      "author": {
        "name": "UNCTAD Trade Facilitation Section",
        "email": "tf-tools@unctad.org"
      },
      "source": "./plugins/bot-mappings",
      "category": "integration"
    },
    {
      "name": "workflow-manager",
      "description": "Discover, execute, and manage BPA workflow orchestrations with dry-run validation, interactive step-by-step execution, retry from failure, and rollback.",
      "author": {
        "name": "UNCTAD Trade Facilitation Section",
        "email": "tf-tools@unctad.org"
      },
      "source": "./plugins/workflow-manager",
      "category": "workflow"
    },
    {
      "name": "notification-designer",
      "description": "Design email, SMS, and push notification templates for BPA service status changes. Gap analysis, variable reference, and multilingual guidance.",
      "author": {
        "name": "UNCTAD Trade Facilitation Section",
        "email": "tf-tools@unctad.org"
      },
      "source": "./plugins/notification-designer",
      "category": "design"
    },
    {
      "name": "classification-manager",
      "description": "Create, manage, and export BPA classification catalogs. Bulk operations, ISO country code application, and CSV export.",
      "author": {
        "name": "UNCTAD Trade Facilitation Section",
        "email": "tf-tools@unctad.org"
      },
      "source": "./plugins/classification-manager",
      "category": "data"
    },
    {
      "name": "data-import",
      "description": "Import bulk data into BPA from CSV or Excel: classification catalogs, document requirements, and cost structures. Idempotent with duplicate detection and error reporting.",
      "author": {
        "name": "UNCTAD Trade Facilitation Section",
        "email": "tf-tools@unctad.org"
      },
      "source": "./plugins/data-import",
      "category": "data"
    },
    {
      "name": "print-document-builder",
      "description": "Design and manage print documents (certificates, licenses, permits). Template-based creation, component ordering, revision history, and rollback.",
      "author": {
        "name": "UNCTAD Trade Facilitation Section",
        "email": "tf-tools@unctad.org"
      },
      "source": "./plugins/print-document-builder",
      "category": "design"
    }
  ]
}
