{
  "name": "undo",
  "version": "0.1.0",
  "description": "Debug and analyze Undo recordings using AI-powered queries",
  "author": {
    "name": "Undo",
    "email": "support@undo.io"
  },
  "homepage": "https://github.com/undoio/addons/tree/master/explain",
  "mcpServers": {
    "undo-debugger": {
      "command": "${CLAUDE_PLUGIN_ROOT}/explain/claude_code_plugin/run.sh",
      "args": []
    }
  },
  "hooks": {
    "PreToolUse": [
      {
        "matcher": "Bash",
        "hooks": [
          {
            "type": "command",
            "command": "${CLAUDE_PLUGIN_ROOT}/explain/claude_code_plugin/hooks/check_debugger_usage.sh"
          }
        ]
      }
    ]
  }
}
