{
  "name": "claude-tmux-hop",
  "description": "Track Claude Code sessions in tmux for quick navigation",
  "version": "0.2.5",
  "author": {
    "name": "unsafe9"
  },
  "repository": "https://github.com/unsafe9/claude-tmux-hop",
  "license": "MIT",
  "keywords": [
    "tmux",
    "navigation",
    "sessions"
  ]
}

files.hooks/hooks.json:
{
  "description": "Track Claude Code session states in tmux for quick navigation",
  "hooks": {
    "SessionStart": [
      {
        "hooks": [
          {
            "type": "command",
            "command": "${CLAUDE_PLUGIN_ROOT}/bin/claude-tmux-hop register --state idle"
          }
        ]
      }
    ],
    "UserPromptSubmit": [
      {
        "hooks": [
          {
            "type": "command",
            "command": "${CLAUDE_PLUGIN_ROOT}/bin/claude-tmux-hop register --state active"
          }
        ]
      }
    ],
    "PreToolUse": [
      {
        "matcher": "AskUserQuestion|ExitPlanMode",
        "hooks": [
          {
            "type": "command",
            "command": "${CLAUDE_PLUGIN_ROOT}/bin/claude-tmux-hop register --state waiting"
          }
        ]
      }
    ],
    "PostToolUse": [
      {
        "matcher": "AskUserQuestion|ExitPlanMode",
        "hooks": [
          {
            "type": "command",
            "command": "${CLAUDE_PLUGIN_ROOT}/bin/claude-tmux-hop register --state active"
          }
        ]
      }
    ],
    "Notification": [
      {
        "matcher": "permission_prompt|elicitation_dialog",
        "hooks": [
          {
            "type": "command",
            "command": "${CLAUDE_PLUGIN_ROOT}/bin/claude-tmux-hop register --state waiting"
          }
        ]
      }
    ],
    "Stop": [
      {
        "hooks": [
          {
            "type": "command",
            "command": "${CLAUDE_PLUGIN_ROOT}/bin/claude-tmux-hop register --state idle"
          }
        ]
      }
    ],
    "SessionEnd": [
      {
        "hooks": [
          {
            "type": "command",
            "command": "${CLAUDE_PLUGIN_ROOT}/bin/claude-tmux-hop clear"
          }
        ]
      }
    ]
  }
}
