{
  "name": "cocosearch",
  "version": "0.1.9",
  "description": "Local-first hybrid semantic code search with MCP tools and workflow skills. Powered by CocoIndex, Tree-sitter, pgvector, and Ollama.",
  "author": {
    "name": "VioletCranberry",
    "email": "zh6an0w.fedor@gmail.com"
  },
  "homepage": "https://github.com/VioletCranberry/coco-s",
  "repository": "https://github.com/VioletCranberry/coco-s",
  "license": "MIT",
  "keywords": [
    "code-search",
    "semantic-search",
    "mcp",
    "cocoindex",
    "tree-sitter",
    "pgvector",
    "ollama"
  ],
  "mcpServers": {
    "cocosearch": {
      "command": "uvx",
      "args": [
        "--from",
        "cocosearch",
        "cocosearch",
        "mcp",
        "--project-from-cwd"
      ]
    }
  }
}
