{
  "$schema": "https://anthropic.com/claude-code/marketplace.schema.json",
  "name": "terraform-lsp-claude-code",
  "description": "Terraform language server plugin for Claude Code - provides code intelligence for infrastructure as code",
  "owner": {
    "name": "vnz",
    "email": "vinz@users.noreply.github.com"
  },
  "plugins": [
    {
      "name": "terraform-lsp",
      "description": "Terraform language server (terraform-ls) for code intelligence, go-to-definition, find-references, and hover docs in Terraform files (.tf, .tfvars, .tftest.hcl)",
      "version": "1.0.0",
      "author": {
        "name": "vnz"
      },
      "source": "./plugins/terraform-lsp",
      "category": "development",
      "license": "MIT",
      "keywords": ["terraform", "lsp", "language-server", "infrastructure", "iac", "hashicorp", "hcl"],
      "strict": false,
      "lspServers": {
        "terraform": {
          "command": "terraform-ls",
          "args": ["serve"],
          "initializationOptions": {
            "experimentalFeatures": {
              "validateOnSave": true,
              "prefillRequiredFields": true
            },
            "validation": {
              "enableEnhancedValidation": true
            }
          },
          "extensionToLanguage": {
            ".tf": "terraform",
            ".tfvars": "terraform",
            ".tftest.hcl": "terraform"
          }
        }
      }
    }
  ]
}
