{
    "$schema": "https://anthropic.com/claude-code/marketplace.schema.json",
    "name": "research-os",
    "version": "0.1.0",
    "description": "Bundled plugins with shared context for research including vision planning, experiment planning and spec- and test-driven implementation workflows.",
    "owner": {
      "name": "Anthropic",
      "email": "support@anthropic.com"
    },
    "plugins": [
      {
        "name": "researcher",
        "description": "Comprehensive Research agents specializing in synthesising hypothesis and claims, researching related work, challenging assumptions, implementing code and planning experiments.",
        "version": "0.1.0",
        "author": {
          "name": "Wieland Brendel",
          "email": "wieland.brendel@tue.ellis.eu"
        },
        "source": "./plugins/researcher",
        "category": "productivity"
      },
      {
        "name": "engineer",
        "description": "Comprehensive Software engineering agents specializing in professional spec- and test-driven development of artifacts.",
        "version": "0.1.0",
            "author": {
                "name": "Wieland Brendel",
                "email": "wieland.brendel@tue.ellis.eu"
        },
        "source": "./plugins/engineer",
        "category": "productivity"
      },
      {
          "name": "presenter",
          "description": "Scientific presentation agents and skills specializing in professional, insightful and engaging research presentations.",
          "version": "0.1.0",
              "author": {
                  "name": "Wieland Brendel",
                  "email": "wieland.brendel@tue.ellis.eu"
          },
          "source": "./plugins/presenter",
          "category": "productivity"
      }
    ]
  }

